import { insertCustomerSchema } from "@shared/schema";
import { z } from "zod";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";

// Frontend customer schema (without tenantId requirement)
const frontendCustomerSchema = insertCustomerSchema.omit({ tenantId: true });

// Types for customer analytics
interface CustomerAnalytics {
  id: string;
  name: string;
  email: string;
  phone: string | null;
  companyId: string | null;
  status: string;
  notes: string | null;
  analytics: {
    totalRevenue: number;
    lifetimeValue: number;
    lifetimeValueCategory: string;
    bookingsCount: number;
    confirmedBookings: number;
    pendingBookings: number;
    cancelledBookings: number;
    averageBookingValue: number;
    lastEventDate: string | null;
    lastEventName: string | null;
    customerSince: string;
  };
}

export default function Customers() {
  const [mobileNavOpen, setMobileNavOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");
  const [companySearchQuery, setCompanySearchQuery] = useState("");
  const [statusFilter, setStatusFilter] = useState<string>("all");
  const [valueFilter, setValueFilter] = useState<string>("all");
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [showCreateCompanyForm, setShowCreateCompanyForm] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [selectedCustomer, setSelectedCustomer] = useState<CustomerAnalytics | null>(null);
  const [editingCompany, setEditingCompany] = useState<Company | null>(null);
  const [viewingCompany, setViewingCompany] = useState<Company | null>(null);
  const [viewingCustomers, setViewingCustomers] = useState<Customer[]>([]);
  const [showAddEmployeeForm, setShowAddEmployeeForm] = useState(false);
  const { toast } = useToast();
  const { formatAmount } = useFormattedCurrency();

  // Fetch customer analytics
  const { data: customerAnalytics = [], isLoading } = useQuery<CustomerAnalytics[]>({
    queryKey: ["/api/customers/analytics"],
  });

  // Fetch companies
  const { data: companies = [], isLoading: companiesLoading } = useQuery<Company[]>({
    queryKey: ["/api/companies"],
  });

  // Fetch all customers for company viewing
  const { data: allCustomers = [] } = useQuery<Customer[]>({
    queryKey: ["/api/customers"],
  });

  const form = useForm({
    resolver: zodResolver(frontendCustomerSchema),
    defaultValues: {
      name: "",
      email: "",
      phone: "",
      companyId: null,
      customerType: "individual",
      status: "lead",
      notes: "",
    }
  });

  // Company form state
  const [companyFormData, setCompanyFormData] = useState({
    name: "",
    industry: "",
    description: "",
    website: "",
    address: "",
    phone: "",
    email: "",
    notes: ""
  });

  // Employees to add when creating/editing company
  const [companyEmployees, setCompanyEmployees] = useState<Array<{
    name: string;
    email: string;
    phone: string;
    status: string;
    notes: string;
  }>>([]);

  // Import functionality state
  const [showImportModal, setShowImportModal] = useState(false);
  const [importType, setImportType] = useState<"customers" | "companies">("customers");
  const [importFile, setImportFile] = useState<File | null>(null);
  const [importPreview, setImportPreview] = useState<any[]>([]);
  const [importErrors, setImportErrors] = useState<string[]>([]);

  // Employee form for adding to company
  const employeeForm = useForm({
    resolver: zodResolver(insertCustomerSchema),
    defaultValues: {
      name: "",
      email: "",
      phone: "",
      companyId: "",
      customerType: "individual",
      status: "customer",
      notes: "",
    }
  });

  const createCustomerMutation = useMutation({
    mutationFn: async (data: any) => {
      console.log("Making API request to create customer:", data);
      const result = await apiRequest("POST", "/api/customers", data);
      console.log("Customer creation result:", result);
      return result;
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/customers/analytics"] });
      queryClient.invalidateQueries({ queryKey: ["/api/customers"] });
      setShowCreateForm(false);
      form.reset();
      toast({
        title: "Success",
        description: "Customer created successfully!",
      });
    },
    onError: (error: any) => {
      console.error("Customer creation error:", error);
      const errorMessage = error?.message || "Failed to create customer. Please try again.";
      toast({
        title: "Error",
        description: errorMessage,
        variant: "destructive",
      });
    },
  });

  // Company mutations
  const createCompanyMutation = useMutation({
    mutationFn: async (data: { companyData: typeof companyFormData; employees: any[] }) => {
      // Create company first
      const company = await apiRequest("POST", "/api/companies", data.companyData);

      // Then create employees if any
      if (data.employees.length > 0) {
        await Promise.all(
          data.employees.map(employee => 
            apiRequest("POST", "/api/customers", {
              ...employee,
              companyId: company.id,
              customerType: "individual"
            })
          )
        );
      }

      return company;
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/companies"] });
      queryClient.invalidateQueries({ queryKey: ["/api/customers"] });
      queryClient.invalidateQueries({ queryKey: ["/api/customers/analytics"] });
      setShowCreateCompanyForm(false);
      resetCompanyForm();
      toast({
        title: "Success",
        description: "Company created successfully",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to create company",
        variant: "destructive",
      });
    },
  });

  const updateCompanyMutation = useMutation({
    mutationFn: async (data: { id: string; updates: Partial<typeof companyFormData>; employees?: any[] }) => {
      // Update company first
      const company = await apiRequest("PATCH", `/api/companies/${data.id}`, data.updates);

      // Then create any new employees if provided
      if (data.employees && data.employees.length > 0) {
        await Promise.all(
          data.employees.map(employee => 
            apiRequest("POST", "/api/customers", {
              ...employee,
              companyId: data.id,
              customerType: "individual"
            })
          )
        );
      }

      return company;
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/companies"] });
      queryClient.invalidateQueries({ queryKey: ["/api/customers"] });
      queryClient.invalidateQueries({ queryKey: ["/api/customers/analytics"] });
      setEditingCompany(null);
      resetCompanyForm();
      toast({
        title: "Success",
        description: "Company updated successfully",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update company",
        variant: "destructive",
      });
    },
  });

  const deleteCompanyMutation = useMutation({
    mutationFn: async (id: string) => {
      return apiRequest("DELETE", `/api/companies/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/companies"] });
      toast({
        title: "Success",
        description: "Company deleted successfully",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to delete company",
        variant: "destructive",
      });
    },
  });

  // Import mutations
  const importDataMutation = useMutation({
    mutationFn: async (data: { type: "customers" | "companies"; items: any[] }) => {
      if (data.type === "customers") {
        // Import customers
        const results = await Promise.allSettled(
          data.items.map(customer => apiRequest("POST", "/api/customers", { ...customer, customerType: "individual" }))
        );
        return results;
      } else {
        // Import companies with employees
        const results = await Promise.allSettled(
          data.items.map(async (company) => {
            const { employees, ...companyData } = company;
            const createdCompany = await apiRequest("POST", "/api/companies", companyData);

            if (employees && employees.length > 0) {
              await Promise.allSettled(
                employees.map((employee: any) => 
                  apiRequest("POST", "/api/customers", {
                    ...employee,
                    companyId: createdCompany.id,
                    customerType: "individual"
                  })
                )
              );
            }

            return createdCompany;
          })
        );
        return results;
      }
    },
    onSuccess: (results) => {
      const successful = results.filter(r => r.status === 'fulfilled').length;
      const failed = results.filter(r => r.status === 'rejected').length;

      queryClient.invalidateQueries({ queryKey: ["/api/customers"] });
      queryClient.invalidateQueries({ queryKey: ["/api/companies"] });
      queryClient.invalidateQueries({ queryKey: ["/api/customers/analytics"] });

      setShowImportModal(false);
      setImportFile(null);
      setImportPreview([]);
      setImportErrors([]);

      toast({
        title: "Import Complete",
        description: `Successfully imported ${successful} items${failed > 0 ? `, ${failed} failed` : ''}`,
      });
    },
    onError: () => {
      toast({
        title: "Import Failed",
        description: "Failed to import data. Please check your file format.",
        variant: "destructive",
      });
    },
  });

  const resetCompanyForm = () => {
    setCompanyFormData({
      name: "",
      industry: "",
      description: "",
      website: "",
      address: "",
      phone: "",
      email: "",
      notes: ""
    });
    setCompanyEmployees([]);
  };

  // Import utility functions
  const parseCSV = (content: string): any[] => {
    const lines = content.trim().split('\n');
    if (lines.length < 2) return [];

    const headers = lines[0].split(',').map(h => h.trim().replace(/"/g, ''));
    const data = [];

    for (let i = 1; i < lines.length; i++) {
      const values = lines[i].split(',').map(v => v.trim().replace(/"/g, ''));
      const row: any = {};
      headers.forEach((header, index) => {
        row[header] = values[index] || '';
      });
      data.push(row);
    }

    return data;
  };

  const validateCustomerData = (data: any[]): { valid: any[]; errors: string[] } => {
    const valid = [];
    const errors = [];

    for (let i = 0; i < data.length; i++) {
      const row = data[i];
      const rowErrors = [];

      if (!row.name || row.name.trim() === '') {
        rowErrors.push(`Row ${i + 2}: Name is required`);
      }
      if (!row.email || row.email.trim() === '') {
        rowErrors.push(`Row ${i + 2}: Email is required`);
      }
      if (row.email && !row.email.includes('@')) {
        rowErrors.push(`Row ${i + 2}: Invalid email format`);
      }

      if (rowErrors.length === 0) {
        valid.push({
          name: row.name,
          email: row.email,
          phone: row.phone || '',
          status: row.status || 'lead',
          notes: row.notes || '',
          companyId: row.companyId || null
        });
      } else {
        errors.push(...rowErrors);
      }
    }

    return { valid, errors };
  };

  const validateCompanyData = (data: any[]): { valid: any[]; errors: string[] } => {
    const valid = [];
    const errors = [];

    for (let i = 0; i < data.length; i++) {
      const row = data[i];
      const rowErrors = [];

      if (!row.name || row.name.trim() === '') {
        rowErrors.push(`Row ${i + 2}: Company name is required`);
      }

      if (rowErrors.length === 0) {
        const company: any = {
          name: row.name,
          industry: row.industry || '',
          description: row.description || '',
          website: row.website || '',
          address: row.address || '',
          phone: row.phone || '',
          email: row.email || '',
          notes: row.notes || ''
        };

        // Parse employees if provided (semicolon separated)
        if (row.employees) {
          const employeesText = row.employees.split(';').filter(e => e.trim());
          company.employees = employeesText.map((empText: string) => {
            const parts = empText.split('|').map(p => p.trim());
            return {
              name: parts[0] || '',
              email: parts[1] || '',
              phone: parts[2] || '',
              status: parts[3] || 'customer',
              notes: parts[4] || ''
            };
          }).filter(emp => emp.name && emp.email);
        }

        valid.push(company);
      } else {
        errors.push(...rowErrors);
      }
    }

    return { valid, errors };
  };

  const handleFileUpload = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    if (!file.name.endsWith('.csv')) {
      toast({
        title: "Invalid File",
        description: "Please upload a CSV file",
        variant: "destructive",
      });
      return;
    }

    setImportFile(file);

    const reader = new FileReader();
    reader.onload = (e) => {
      const content = e.target?.result as string;
      const parsedData = parseCSV(content);

      if (importType === 'customers') {
        const { valid, errors } = validateCustomerData(parsedData);
        setImportPreview(valid);
        setImportErrors(errors);
      } else {
        const { valid, errors } = validateCompanyData(parsedData);
        setImportPreview(valid);
        setImportErrors(errors);
      }
    };

    reader.readAsText(file);
  };

  const handleImport = () => {
    if (importPreview.length === 0) {
      toast({
        title: "No Data",
        description: "No valid data to import",
        variant: "destructive",
      });
      return;
    }

    importDataMutation.mutate({
      type: importType,
      items: importPreview
    });
  };

  const resetImportModal = () => {
    setImportFile(null);
    setImportPreview([]);
    setImportErrors([]);
    setImportType("customers");
  };

  const addCompanyEmployee = () => {
    setCompanyEmployees([...companyEmployees, {
      name: "",
      email: "",
      phone: "",
      status: "customer",
      notes: ""
    }]);
  };

  const updateCompanyEmployee = (index: number, field: string, value: string) => {
