import { useState } from "react";
import { Sidebar } from "@/components/layout/sidebar";
import { Header } from "@/components/layout/header";
import { MobileNav } from "@/components/layout/mobile-nav";
import { useFormattedCurrency } from "@/lib/currency";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { useQuery } from "@tanstack/react-query";
import { CreditCard, DollarSign, TrendingUp, AlertCircle, CheckCircle } from "lucide-react";
import { format } from "date-fns";

export default function Payments() {
  const [mobileNavOpen, setMobileNavOpen] = useState(false);
  const { formatAmount } = useFormattedCurrency();
  const { data: payments, isLoading } = useQuery({
    queryKey: ["/api/payments"],
  });

  const getStatusColor = (status: string) => {
    // Payment statuses use different colors than event statuses
    switch (status) {
      case "completed": return "bg-green-100 text-green-800";
      case "pending": return "bg-yellow-100 text-yellow-800";
      case "failed": return "bg-red-100 text-red-800";
      case "refunded": return "bg-blue-100 text-blue-800";
      default: return "bg-gray-100 text-gray-800";
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case "completed": return <CheckCircle className="w-4 h-4" />;
      case "failed": return <AlertCircle className="w-4 h-4" />;
      default: return <CreditCard className="w-4 h-4" />;
    }
  };

  const getPaymentTypeColor = (type: string) => {
    switch (type) {
      case "deposit": return "text-blue-600";
      case "final": return "text-green-600";
      case "refund": return "text-orange-600";
      default: return "text-gray-600";
    }
  };

  // Calculate metrics
  const totalRevenue = payments?.reduce((sum: number, payment: any) => 
    payment.status === "completed" ? sum + parseFloat(payment.amount) : sum, 0) || 0;

  const pendingPayments = payments?.filter((p: any) => p.status === "pending").length || 0;
  const completedPayments = payments?.filter((p: any) => p.status === "completed").length || 0;

  if (isLoading) {
    return (
      <div className="flex h-screen overflow-hidden bg-slate-50">
        <Sidebar />
        <div className="flex-1 flex flex-col overflow-hidden">
          <Header title="Payments" subtitle="Track and manage all payments" />
          <main className="flex-1 overflow-y-auto p-6">
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
              {[...Array(3)].map((_, i) => (
                <Card key={i} className="animate-pulse">
                  <CardContent className="p-6">
                    <div className="h-4 bg-gray-200 rounded mb-2"></div>
                    <div className="h-8 bg-gray-200 rounded"></div>
                  </CardContent>
                </Card>
              ))}
            </div>
          </main>
        </div>
      </div>
    );
  }

  return (
    <div className="flex h-screen overflow-hidden bg-slate-50">
      <div className="hidden lg:block">
        <Sidebar />
      </div>

      <MobileNav 
        isOpen={mobileNavOpen} 
        onClose={() => setMobileNavOpen(false)} 
      />

      <div className="flex-1 flex flex-col overflow-hidden">
        <Header 
          title="Payments" 
          subtitle="Track and manage all payments"
          onMobileMenuToggle={() => setMobileNavOpen(true)}
          action={
            <Button className="bg-blue-600 hover:bg-blue-700">
              + Process Payment
            </Button>
          }
        />

        <main className="flex-1 overflow-y-auto p-6">
          {/* Payment Metrics */}
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
            <Card>
              <CardContent className="p-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-sm font-medium text-gray-600">Total Revenue</p>
                    <p className="text-3xl font-bold text-gray-900">{formatAmount(totalRevenue)}</p>
                    <p className="text-sm text-green-600 font-medium">+8% from last month</p>
                  </div>
                  <div className="w-12 h-12 bg-green-50 rounded-lg flex items-center justify-center">
                    <DollarSign className="w-6 h-6 text-green-600" />
                  </div>
                </div>
              </CardContent>
            </Card>

            <Card>
              <CardContent className="p-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-sm font-medium text-gray-600">Completed Payments</p>
                    <p className="text-3xl font-bold text-gray-900">{completedPayments}</p>
                    <p className="text-sm text-blue-600 font-medium">This month</p>
                  </div>
                  <div className="w-12 h-12 bg-blue-50 rounded-lg flex items-center justify-center">
                    <CheckCircle className="w-6 h-6 text-blue-600" />
                  </div>
                </div>
              </CardContent>
            </Card>

            <Card>
              <CardContent className="p-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-sm font-medium text-gray-600">Pending Payments</p>
                    <p className="text-3xl font-bold text-gray-900">{pendingPayments}</p>
                    <p className="text-sm text-orange-600 font-medium">Awaiting processing</p>
                  </div>
                  <div className="w-12 h-12 bg-orange-50 rounded-lg flex items-center justify-center">
                    <AlertCircle className="w-6 h-6 text-orange-600" />
                  </div>
                </div>
              </CardContent>
            </Card>
          </div>

          {/* Payments List */}
          <Card>
            <CardHeader>
              <CardTitle>Recent Payments</CardTitle>
            </CardHeader>
            <CardContent>
              {!payments || payments.length === 0 ? (
                <div className="text-center py-12">
                  <CreditCard className="mx-auto h-12 w-12 text-gray-400 mb-4" />
                  <h3 className="text-lg font-medium text-gray-900 mb-2">No payments yet</h3>
                  <p className="text-gray-600 mb-6">Payment transactions will appear here once processed.</p>
                  <Button className="bg-blue-600 hover:bg-blue-700">
                    Process First Payment
                  </Button>
                </div>
              ) : (
                <div className="overflow-x-auto">
                  <table className="w-full">
                    <thead>
                      <tr className="border-b">
                        <th className="text-left py-3 font-medium text-gray-600">Transaction ID</th>
                        <th className="text-left py-3 font-medium text-gray-600">Amount</th>
                        <th className="text-left py-3 font-medium text-gray-600">Type</th>
                        <th className="text-left py-3 font-medium text-gray-600">Method</th>
                        <th className="text-left py-3 font-medium text-gray-600">Status</th>
                        <th className="text-left py-3 font-medium text-gray-600">Date</th>
                        <th className="text-left py-3 font-medium text-gray-600">Actions</th>
                      </tr>
                    </thead>
                    <tbody>
                      {payments.map((payment: any) => (
                        <tr key={payment.id} className="border-b hover:bg-gray-50">
                          <td className="py-4">
                            <div className="font-mono text-sm">
                              {payment.transactionId || payment.id.slice(-8)}
                            </div>
                          </td>
                          <td className="py-4">
                            <div className="font-semibold text-lg">
                              {formatAmount(parseFloat(payment.amount))}
                            </div>
                          </td>
                          <td className="py-4">
                            <span className={`capitalize ${getPaymentTypeColor(payment.paymentType)}`}>
                              {payment.paymentType}
                            </span>
                          </td>
                          <td className="py-4 capitalize">{payment.paymentMethod.replace('_', ' ')}</td>
                          <td className="py-4">
                            <Badge className={`${getStatusColor(payment.status)} flex items-center gap-1 w-fit`}>
                              {getStatusIcon(payment.status)}
                              {payment.status}
                            </Badge>
                          </td>
                          <td className="py-4">
                            {payment.processedAt 
                              ? format(new Date(payment.processedAt), "MMM dd, yyyy")
                              : format(new Date(payment.createdAt), "MMM dd, yyyy")
                            }
                          </td>
                          <td className="py-4">
                            <Button variant="outline" size="sm">
                              View Details
                            </Button>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              )}
            </CardContent>
          </Card>
        </main>
      </div>
    </div>
  );
}

client/src/pages/proposals.tsx.content:
import { useState } from "react";
import { Sidebar } from "@/components/layout/sidebar";
import { Header } from "@/components/layout/header";
import { MobileNav } from "@/components/layout/mobile-nav";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { useQuery } from "@tanstack/react-query";
import { ProposalTrackingModal } from "@/components/proposals/proposal-tracking-modal";
import { format } from "date-fns";
import { 
  FileText, 
  Eye, 
  Mail, 
  DollarSign, 
  Clock, 
  TrendingUp,
  Users,
  CheckCircle2,
  XCircle,
  Send,
  Edit,
  MoreHorizontal
} from "lucide-react";

export default function Proposals() {
  const [mobileNavOpen, setMobileNavOpen] = useState(false);
  const [showTrackingModal, setShowTrackingModal] = useState(false);
  const [selectedProposalId, setSelectedProposalId] = useState("");

  // Fetch proposals data
  const { data: proposals = [], isLoading } = useQuery({ 
    queryKey: ["/api/proposals"] 
  });

  // Calculate proposal metrics
  const metrics = {
    total: proposals.length,
    sent: proposals.filter((p: any) => p.status !== 'draft').length,
    viewed: proposals.filter((p: any) => p.emailOpened).length,
    accepted: proposals.filter((p: any) => p.status === 'accepted').length,
    converted: proposals.filter((p: any) => p.status === 'converted').length,
    totalValue: proposals.reduce((sum: number, p: any) => sum + parseFloat(p.totalAmount || 0), 0),
    depositsPaid: proposals.filter((p: any) => p.depositPaid).length
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'draft': return 'bg-gray-100 text-gray-800';
      case 'sent': return 'bg-blue-100 text-blue-800';
      case 'viewed': return 'bg-purple-100 text-purple-800';
      case 'accepted': return 'bg-green-100 text-green-800';
      case 'rejected': return 'bg-red-100 text-red-800';
      case 'converted': return 'bg-emerald-100 text-emerald-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'sent': return <Mail className="h-4 w-4" />;
      case 'viewed': return <Eye className="h-4 w-4" />;
      case 'accepted': return <CheckCircle2 className="h-4 w-4" />;
      case 'rejected': return <XCircle className="h-4 w-4" />;
      case 'converted': return <CheckCircle2 className="h-4 w-4" />;
      default: return <FileText className="h-4 w-4" />;
    }
  };

  const handleViewProposal = (proposalId: string) => {
    setSelectedProposalId(proposalId);
    setShowTrackingModal(true);
  };

  // Filter proposals based on status
  const getFilteredProposals = (status: string) => {
    switch (status) {
      case 'draft': return proposals.filter((p: any) => p.status === 'draft');
      case 'sent': return proposals.filter((p: any) => p.status === 'sent');
      case 'viewed': return proposals.filter((p: any) => p.emailOpened);
      case 'accepted': return proposals.filter((p: any) => p.status === 'accepted');
      default: return proposals;
    }
  };

  // Render proposal table/cards
  const renderProposalsList = (filteredProposals: any[]) => (
    <>
      {/* Mobile Cards View */}
      <div className="sm:hidden space-y-3">
        {filteredProposals.length === 0 ? (
          <div className="text-center py-8">
            <div className="flex flex-col items-center gap-3">
              <FileText className="h-12 w-12 text-gray-300" />
              <div>
                <p className="font-medium text-gray-900">No proposals found</p>
                <p className="text-sm text-gray-500">Proposals sent through the event creation process will appear here</p>
              </div>
            </div>
          </div>
        ) : (
          filteredProposals.map((proposal: any) => (
            <Card key={proposal.id} className="p-3">
              <div className="space-y-2">
                <div className="flex items-start justify-between">
                  <div className="flex-1">
                    <h3 className="font-medium text-sm">{proposal.title}</h3>
                    <p className="text-xs text-gray-600">{proposal.customerName}</p>
                  </div>
                  <Badge className={`${getStatusColor(proposal.status)} text-xs`}>
                    <div className="flex items-center gap-1">
                      {getStatusIcon(proposal.status)}
                      {proposal.status}
                    </div>
                  </Badge>
                </div>

                <div className="grid grid-cols-2 gap-2 text-xs">
                  <div>
                    <span className="text-gray-500">Date:</span>
                    <div>{proposal.eventDate ? format(new Date(proposal.eventDate), 'MMM d, yyyy') : 'N/A'}</div>
                  </div>
                  <div>
                    <span className="text-gray-500">Amount:</span>
                    <div className="font-medium">${parseFloat(proposal.totalAmount || 0).toLocaleString()}</div>
                  </div>
                  <div>
                    <span className="text-gray-500">Created:</span>
                    <div>{format(new Date(proposal.createdAt), 'MMM d')}</div>
                  </div>
                  <div>
                    <span className="text-gray-500">Deposit:</span>
                    <div className={proposal.depositPaid ? "text-green-600" : "text-gray-500"}>
                      {proposal.depositPaid ? "Paid" : "Pending"}
                    </div>
                  </div>
                </div>

                <div className="flex gap-2 pt-2">
                  <Button
                    size="sm"
                    variant="outline"
                    onClick={() => handleViewProposal(proposal.id)}
                    className="flex-1 text-xs"
                  >
                    <Eye className="h-3 w-3 mr-1" />
                    View
                  </Button>
                  <Button size="sm" variant="outline" className="flex-1 text-xs">
                    <Edit className="h-3 w-3 mr-1" />
                    Edit
                  </Button>
                </div>
              </div>
            </Card>
          ))
        )}
      </div>

      {/* Desktop Table View */}
      <div className="hidden sm:block border rounded-lg">
        <Table>
          <TableHeader>
            <TableRow>
              <TableHead className="text-xs sm:text-sm">Proposal</TableHead>
              <TableHead className="text-xs sm:text-sm">Customer</TableHead>
              <TableHead className="text-xs sm:text-sm">Event Date</TableHead>
              <TableHead className="text-xs sm:text-sm">Amount</TableHead>
              <TableHead className="text-xs sm:text-sm">Status</TableHead>
              <TableHead className="text-xs sm:text-sm">Deposit</TableHead>
              <TableHead className="text-xs sm:text-sm">Created</TableHead>
              <TableHead className="text-xs sm:text-sm">Actions</TableHead>
            </TableRow>
          </TableHeader>
          <TableBody>
            {filteredProposals.length === 0 ? (
              <TableRow>
                <TableCell colSpan={8} className="text-center py-8">
                  <div className="flex flex-col items-center gap-3">
                    <FileText className="h-12 w-12 text-gray-300" />
                    <div>
                      <p className="font-medium text-gray-900">No proposals found</p>
                      <p className="text-sm text-gray-500">Proposals sent through the event creation process will appear here</p>
                    </div>
                  </div>
                </TableCell>
              </TableRow>
            ) : (
              filteredProposals.map((proposal: any) => (
                <TableRow 
                  key={proposal.id}
                  className="hover:bg-gray-50 cursor-pointer"
                  onClick={() => handleViewProposal(proposal.id)}
                >
                  <TableCell>
                    <div>
                      <div className="font-medium">{proposal.title}</div>
                      <div className="text-sm text-gray-500">{proposal.eventName}</div>
                    </div>
                  </TableCell>
                  <TableCell>
                    <div>
                      <div className="font-medium">{proposal.customer?.name}</div>
                      <div className="text-sm text-gray-500">{proposal.customer?.email}</div>
                    </div>
                  </TableCell>
                  <TableCell>
                    {proposal.eventDate 
                      ? format(new Date(proposal.eventDate), "MMM d, yyyy")
                      : "TBD"
                    }
                  </TableCell>
                  <TableCell className="font-medium">
                    ${parseFloat(proposal.totalAmount).toFixed(2)}
                  </TableCell>
                  <TableCell>
                    <Badge className={getStatusColor(proposal.status)}>
                      {getStatusIcon(proposal.status)}
                      <span className="ml-1">{proposal.status.charAt(0).toUpperCase() + proposal.status.slice(1)}</span>
                    </Badge>
                  </TableCell>
                  <TableCell>
                    <div className="text-sm">
                      <div className="font-medium text-green-600">
                        ${parseFloat(proposal.depositAmount).toFixed(2)}
                      </div>
                      {proposal.depositPaid ? (
                        <Badge className="bg-green-100 text-green-800 text-xs">
                          âœ“ Paid
                        </Badge>
                      ) : (
                        <Badge variant="outline" className="text-xs">
                          Pending
                        </Badge>
                      )}
                    </div>
                  </TableCell>
                  <TableCell className="text-sm text-gray-500">
                    {format(new Date(proposal.createdAt), "MMM d, yyyy")}
                  </TableCell>
                  <TableCell>
                    <div className="flex items-center gap-2">
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={(e) => {
                          e.stopPropagation();
                          handleViewProposal(proposal.id);
                        }}
                      >
                        <Eye className="h-4 w-4" />
                      </Button>
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={(e) => e.stopPropagation()}
                      >
                        <MoreHorizontal className="h-4 w-4" />
                      </Button>
                    </div>
                  </TableCell>
                </TableRow>
              ))
            )}
          </TableBody>
        </Table>
      </div>
    </>
  );

  if (isLoading) {
    return (
      <div className="flex h-screen overflow-hidden bg-slate-50">
        <div className="hidden lg:block">
          <Sidebar />
        </div>
        <div className="flex-1 flex items-center justify-center">
          <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full" />
        </div>
      </div>
    );
  }

  return (
    <div className="flex h-screen overflow-hidden bg-slate-50">
      <div className="hidden lg:block">
        <Sidebar />
      </div>

      <MobileNav 
        isOpen={mobileNavOpen} 
        onClose={() => setMobileNavOpen(false)} 
      />

      <div className="flex-1 flex flex-col overflow-hidden">
        <Header 
          title="Proposals" 
          subtitle="Track and manage sent event proposals"
          onMobileMenuToggle={() => setMobileNavOpen(true)}
        />

        <main className="flex-1 overflow-y-auto p-3 sm:p-6">
          {/* Metrics Overview */}
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-3 sm:gap-6 mb-4 sm:mb-6">
            <Card>
              <CardContent className="p-3 sm:p-4">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-xs sm:text-sm font-medium text-gray-600">Total Proposals</p>
                    <div className="text-xl sm:text-2xl font-bold">{metrics.total}</div>
                  </div>
                  <div className="bg-blue-100 rounded-full p-2 sm:p-3">
                    <FileText className="h-4 w-4 sm:h-6 sm:w-6 text-blue-600" />
                  </div>
                </div>
              </CardContent>
            </Card>

            <Card>
              <CardContent className="p-3 sm:p-4">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-xs sm:text-sm font-medium text-gray-600">Viewed Rate</p>
                    <div className="text-xl sm:text-2xl font-bold">
                      {metrics.sent > 0 ? Math.round((metrics.viewed / metrics.sent) * 100) : 0}%
                    </div>
                  </div>
                  <div className="bg-purple-100 rounded-full p-2 sm:p-3">
                    <Eye className="h-4 w-4 sm:h-6 sm:w-6 text-purple-600" />
                  </div>
                </div>
              </CardContent>
            </Card>

            <Card>
              <CardContent className="p-3 sm:p-4">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-xs sm:text-sm font-medium text-gray-600">Acceptance Rate</p>
                    <div className="text-xl sm:text-2xl font-bold">
                      {metrics.sent > 0 ? Math.round((metrics.accepted / metrics.sent) * 100) : 0}%
                    </div>
                  </div>
                  <div className="bg-green-100 rounded-full p-2 sm:p-3">
                    <CheckCircle2 className="h-4 w-4 sm:h-6 sm:w-6 text-green-600" />
                  </div>
                </div>
              </CardContent>
            </Card>

            <Card>
              <CardContent className="p-3 sm:p-4">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-xs sm:text-sm font-medium text-gray-600">Total Value</p>
                    <div className="text-xl sm:text-2xl font-bold">${metrics.totalValue.toLocaleString()}</div>
                  </div>
                  <div className="bg-emerald-100 rounded-full p-2 sm:p-3">
                    <DollarSign className="h-4 w-4 sm:h-6 sm:w-6 text-emerald-600" />
                  </div>
                </div>
              </CardContent>
            </Card>
          </div>

          {/* Proposals Table */}
          <Card>
            <CardHeader className="pb-3">
              <CardTitle className="flex items-center gap-2 text-base sm:text-lg">
                <FileText className="h-4 w-4 sm:h-5 sm:w-5" />
                All Proposals
              </CardTitle>
            </CardHeader>
            <CardContent>
              <Tabs defaultValue="all" className="space-y-3 sm:space-y-4">
                <TabsList className="grid w-full grid-cols-5 text-xs sm:text-sm">
                  <TabsTrigger value="all" className="px-2 sm:px-4">All ({metrics.total})</TabsTrigger>
                  <TabsTrigger value="draft" className="px-2 sm:px-4">Drafts ({proposals.filter((p: any) => p.status === 'draft').length})</TabsTrigger>
                  <TabsTrigger value="sent" className="px-2 sm:px-4">Sent ({metrics.sent})</TabsTrigger>
                  <TabsTrigger value="viewed" className="px-2 sm:px-4">Viewed ({metrics.viewed})</TabsTrigger>
                  <TabsTrigger value="accepted" className="px-2 sm:px-4">Accepted ({metrics.accepted})</TabsTrigger>
                </TabsList>

                <TabsContent value="all">
                  {renderProposalsList(getFilteredProposals('all'))}
                </TabsContent>

                <TabsContent value="draft">
                  {renderProposalsList(getFilteredProposals('draft'))}
                </TabsContent>

                <TabsContent value="sent">
                  {renderProposalsList(getFilteredProposals('sent'))}
                </TabsContent>

                <TabsContent value="viewed">
                  {renderProposalsList(getFilteredProposals('viewed'))}
