                                      feeAmount = serviceSubtotal * (parseFloat(fee.value) / 100);
                                    } else {
                                      feeAmount = parseFloat(fee.value);
                                    }
                                    totalFees += feeAmount;
                                    appliedFees.push({ name: fee.name, amount: feeAmount });
