import { useState, useRef } from "react";
import { Dialog, DialogContent, DialogTitle, DialogDescription } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Card } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Mic, MicOff, Play, Square, Sparkles, Calendar, MapPin, Users, Clock } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { format, parse } from "date-fns";

interface Props {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  onEventCreated?: () => void;
}

interface VoiceBookingData {
  eventName: string;
  eventDate: string;
  startTime: string;
  endTime: string;
  guestCount: number;
  eventType: string;
  customerName: string;
  customerEmail: string;
  customerPhone: string;
  specialRequests: string;
  suggestedVenue: string;
  suggestedServices: string[];
  confidence?: number;
  corrections?: string[];
}

export function VoiceBookingModal({ open, onOpenChange, onEventCreated }: Props) {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [isRecording, setIsRecording] = useState(false);
  const [transcript, setTranscript] = useState("");
  const [isProcessing, setIsProcessing] = useState(false);
  const [extractedData, setExtractedData] = useState<VoiceBookingData | null>(null);
  const [audioBlob, setAudioBlob] = useState<Blob | null>(null);
  const mediaRecorderRef = useRef<MediaRecorder | null>(null);
  const audioChunksRef = useRef<Blob[]>([]);

  const processVoiceBooking = useMutation({
    mutationFn: async (transcriptText: string) => {
      const response = await apiRequest("POST", "/api/ai/parse-voice", {
        transcript: transcriptText
      });
      return response.json();
    },
    onSuccess: (data) => {
      // Convert the parsed data to match our VoiceBookingData interface
      const formattedData = {
        eventName: data.eventName || "",
        eventDate: data.eventDate || "",
        startTime: data.startTime || "",
        endTime: data.endTime || "",
        guestCount: data.guestCount || 0,
        eventType: data.eventType || "",
        customerName: data.customerName || "",
        customerEmail: data.customerEmail || "",
        customerPhone: data.customerPhone || "",
        specialRequests: data.specialRequests || "",
        suggestedVenue: data.suggestedVenue || "",
        suggestedServices: data.suggestedServices || []
      };

      setExtractedData(formattedData);
      setIsProcessing(false);

      // Show corrections made by AI if any
      const correctionsMessage = data.corrections && data.corrections.length > 0 
        ? `AI made corrections: ${data.corrections.join(", ")}`
        : "AI successfully extracted booking details";

      toast({
        title: "Voice booking processed!",
        description: `${correctionsMessage}. Confidence: ${data.confidence || 0}%`
      });
    },
    onError: (error: any) => {
      setIsProcessing(false);
      toast({
        title: "Processing failed",
        description: error.message || "Failed to process voice booking",
        variant: "destructive"
      });
    }
  });

  const createBookingFromVoice = useMutation({
    mutationFn: async (bookingData: any) => {
      const response = await apiRequest("POST", "/api/bookings", bookingData);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/bookings"] });
      queryClient.invalidateQueries({ queryKey: ["/api/calendar/events"] });
      toast({
        title: "Booking created successfully!",
        description: "Voice booking has been converted to an event."
      });
      onEventCreated?.();
      onOpenChange(false);
      resetForm();
    },
    onError: (error: any) => {
      toast({
        title: "Failed to create booking",
        description: error.message,
        variant: "destructive"
      });
    }
  });

  const startRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const mediaRecorder = new MediaRecorder(stream);
      mediaRecorderRef.current = mediaRecorder;
      audioChunksRef.current = [];

      mediaRecorder.ondataavailable = (event) => {
        if (event.data.size > 0) {
          audioChunksRef.current.push(event.data);
        }
      };

      mediaRecorder.onstop = () => {
        const audioBlob = new Blob(audioChunksRef.current, { type: 'audio/wav' });
        setAudioBlob(audioBlob);

        // Start speech recognition
        startSpeechRecognition();
      };

      mediaRecorder.start();
      setIsRecording(true);
      toast({
        title: "Recording started",
        description: "Speak clearly about your event booking details"
      });
    } catch (error) {
      toast({
        title: "Recording failed",
        description: "Could not access microphone",
        variant: "destructive"
      });
    }
  };

  const stopRecording = () => {
    if (mediaRecorderRef.current && isRecording) {
      mediaRecorderRef.current.stop();
      setIsRecording(false);

      // Stop all tracks
      mediaRecorderRef.current.stream?.getTracks().forEach(track => track.stop());

      toast({
        title: "Recording stopped",
        description: "Processing your voice booking..."
      });
    }
  };

  const startSpeechRecognition = () => {
    if (!('webkitSpeechRecognition' in window) && !('SpeechRecognition' in window)) {
      toast({
        title: "Speech recognition not supported",
        description: "Please use Chrome, Safari, or Edge for voice booking",
        variant: "destructive"
      });
      return;
    }

    const SpeechRecognition = (window as any).SpeechRecognition || (window as any).webkitSpeechRecognition;
    const recognition = new SpeechRecognition();

    recognition.continuous = true;
    recognition.interimResults = true;
    recognition.lang = 'en-US';
    recognition.maxAlternatives = 3; // Get multiple alternatives for better accuracy

    let finalTranscript = '';

    recognition.onresult = (event: any) => {
      let interimTranscript = '';

      for (let i = event.resultIndex; i < event.results.length; i++) {
        const transcript = event.results[i][0].transcript;
        if (event.results[i].isFinal) {
          finalTranscript += transcript + ' ';
        } else {
          interimTranscript += transcript;
        }
      }

      // Update display with interim results
      setTranscript(finalTranscript + interimTranscript);
    };

    recognition.onend = () => {
      if (finalTranscript.trim()) {
        setIsProcessing(true);
        toast({
          title: "Processing speech...",
          description: "AI is analyzing and correcting your voice input"
        });
        processVoiceBooking.mutate(finalTranscript.trim());
      } else {
        toast({
          title: "No speech detected",
          description: "Please try recording again and speak clearly",
          variant: "destructive"
        });
      }
    };

    recognition.onerror = (event: any) => {
      const errorMessages = {
        'no-speech': 'No speech was detected. Please try again.',
        'audio-capture': 'No microphone was found. Please check your microphone.',
        'not-allowed': 'Microphone permission denied. Please allow microphone access.',
        'network': 'Network error occurred. Please check your connection.',
        'aborted': 'Speech recognition was aborted.',
        'language-not-supported': 'Language not supported.',
        'service-not-allowed': 'Speech recognition service not allowed.'
      };

      const errorMessage = errorMessages[event.error as keyof typeof errorMessages] || 'Speech recognition error occurred';

      toast({
        title: "Speech Recognition Error",
        description: errorMessage,
        variant: "destructive"
      });
    };

    recognition.start();
  };

  const confirmBooking = () => {
    if (!extractedData) return;

    // Check for missing required fields and suggest them
    const missingFields = [];
    if (!extractedData.eventName) missingFields.push("event name");
    if (!extractedData.eventDate) missingFields.push("event date");
    if (!extractedData.customerName) missingFields.push("customer name");
    if (!extractedData.customerEmail) missingFields.push("customer email");

    if (missingFields.length > 0) {
      toast({
        title: "Missing Information",
        description: `Please provide: ${missingFields.join(", ")}. Try recording again with these details.`,
        variant: "destructive"
      });
      return;
    }

    // Format the booking data properly for the API
    const bookingData = {
      eventName: extractedData.eventName,
      eventType: extractedData.eventType || "Meeting", 
      eventDate: extractedData.eventDate, // API expects ISO date string or Date object
      startTime: extractedData.startTime || "09:00",
      endTime: extractedData.endTime || "17:00",
      guestCount: parseInt(extractedData.guestCount?.toString() || "1"),
      status: "inquiry",
      customerName: extractedData.customerName,
      customerEmail: extractedData.customerEmail,
      customerPhone: extractedData.customerPhone || "",
      notes: extractedData.specialRequests || "Created via AI Voice Booking",
      // Default to first venue if no match found  
      venueId: "5337f504-a61b-442a-9e8b-9e197c421aca", // Grand Ballroom ID
      spaceId: "space-1", // Default space
      selectedServices: extractedData.suggestedServices || [],
      totalAmount: "0", // Will be calculated by the API
      depositAmount: "0"
    };

    createBookingFromVoice.mutate(bookingData);
  };

  const resetForm = () => {
    setTranscript("");
    setExtractedData(null);
    setAudioBlob(null);
    setIsProcessing(false);
    setIsRecording(false);
  };

  const playRecording = () => {
    if (audioBlob) {
      const audioUrl = URL.createObjectURL(audioBlob);
      const audio = new Audio(audioUrl);
      audio.play();
    }
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="max-w-4xl max-h-[85vh] overflow-y-auto">
        <DialogTitle className="flex items-center gap-2">
          <Sparkles className="h-5 w-5 text-purple-600" />
          AI Voice Booking
        </DialogTitle>
        <DialogDescription>
          Speak naturally about your event booking and let AI extract all the details for you.
        </DialogDescription>

        <div className="space-y-6">
          {/* Recording Section */}
          <Card className="p-6">
            <div className="text-center space-y-4">
              <div className="flex justify-center">
                <div className={`w-20 h-20 rounded-full flex items-center justify-center ${
                  isRecording ? 'bg-red-100 animate-pulse' : 'bg-purple-100'
                }`}>
                  {isRecording ? (
                    <MicOff className="w-8 h-8 text-red-600" />
                  ) : (
                    <Mic className="w-8 h-8 text-purple-600" />
                  )}
                </div>
              </div>

              <div className="space-y-2">
                <h3 className="font-semibold">
                  {isRecording ? "Recording..." : "Ready to Record"}
                </h3>
                <p className="text-sm text-gray-600">
                  {isRecording 
                    ? "Speak clearly about your event details, venue preferences, and requirements" 
                    : "Click start and describe your event booking in natural language"
                  }
                </p>
              </div>

              <div className="flex justify-center gap-3">
                {!isRecording ? (
                  <Button onClick={startRecording} className="bg-purple-600 hover:bg-purple-700">
                    <Mic className="w-4 h-4 mr-2" />
                    Start Recording
                  </Button>
                ) : (
                  <Button onClick={stopRecording} variant="destructive">
                    <Square className="w-4 h-4 mr-2" />
                    Stop Recording
                  </Button>
                )}

                {audioBlob && (
                  <Button onClick={playRecording} variant="outline">
                    <Play className="w-4 h-4 mr-2" />
                    Play Recording
                  </Button>
                )}
              </div>
            </div>
          </Card>

          {/* Transcript Section */}
          {transcript && (
            <Card className="p-4">
              <h4 className="font-medium mb-2 flex items-center gap-2">
                <Sparkles className="w-4 h-4 text-purple-600" />
                Voice Transcript
              </h4>
              <p className="text-sm text-gray-700 bg-gray-50 p-3 rounded italic">
                "{transcript}"
              </p>
            </Card>
          )}

          {/* Processing Indicator */}
          {isProcessing && (
            <Card className="p-4 text-center">
              <div className="flex items-center justify-center gap-2">
                <Sparkles className="w-5 h-5 text-purple-600 animate-spin" />
                <span>AI is extracting booking details...</span>
              </div>
            </Card>
          )}

          {/* Extracted Data Section */}
          {extractedData && (
            <Card className="p-6">
              <div className="flex items-center justify-between mb-4">
                <h4 className="font-semibold flex items-center gap-2">
                  <Sparkles className="w-4 h-4 text-purple-600" />
                  Extracted Booking Details
                </h4>
                <div className="flex gap-2">
                  {extractedData.confidence && (
                    <Badge variant="outline" className={`${
                      extractedData.confidence >= 80 ? 'bg-green-100 text-green-800' :
                      extractedData.confidence >= 60 ? 'bg-yellow-100 text-yellow-800' :
                      'bg-red-100 text-red-800'
                    }`}>
                      {extractedData.confidence}% Confidence
                    </Badge>
                  )}
                  <Badge className="bg-purple-100 text-purple-800">
                    AI Processed
                  </Badge>
                </div>
              </div>

              {/* AI Corrections Display */}
              {extractedData.corrections && extractedData.corrections.length > 0 && (
                <div className="mb-4 p-3 bg-blue-50 rounded border border-blue-200">
                  <p className="font-medium text-blue-800 mb-1">AI Made These Corrections:</p>
                  <ul className="text-sm text-blue-700 list-disc list-inside">
                    {extractedData.corrections.map((correction, index) => (
                      <li key={index}>{correction}</li>
                    ))}
                  </ul>
                </div>
              )}

              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div className="space-y-3">
                  <div className="flex items-center gap-2">
                    <Calendar className="w-4 h-4 text-blue-600" />
                    <div>
                      <p className="font-medium">{extractedData.eventName}</p>
                      <p className="text-sm text-gray-600">{extractedData.eventType}</p>
                    </div>
                  </div>

                  <div className="flex items-center gap-2">
                    <Clock className="w-4 h-4 text-green-600" />
                    <div>
                      <p className="font-medium">{extractedData.eventDate}</p>
                      <p className="text-sm text-gray-600">
                        {extractedData.startTime} - {extractedData.endTime}
                      </p>
                    </div>
                  </div>

                  <div className="flex items-center gap-2">
                    <Users className="w-4 h-4 text-orange-600" />
                    <div>
                      <p className="font-medium">{extractedData.guestCount} Guests</p>
                    </div>
                  </div>
                </div>

                <div className="space-y-3">
                  <div>
                    <p className="font-medium">Customer</p>
                    <p className="text-sm text-gray-600">{extractedData.customerName}</p>
                    <p className="text-sm text-gray-600">{extractedData.customerEmail}</p>
                    <p className="text-sm text-gray-600">{extractedData.customerPhone}</p>
                  </div>

                  {extractedData.suggestedVenue && (
                    <div>
                      <p className="font-medium">Suggested Venue</p>
                      <p className="text-sm text-gray-600">{extractedData.suggestedVenue}</p>
                    </div>
                  )}

                  {extractedData.suggestedServices.length > 0 && (
                    <div>
                      <p className="font-medium mb-2">Suggested Services</p>
                      <div className="flex flex-wrap gap-1">
                        {extractedData.suggestedServices.map((service, index) => (
                          <Badge key={index} variant="outline" className="text-xs">
                            {service}
                          </Badge>
                        ))}
                      </div>
                    </div>
                  )}
                </div>
              </div>

              {extractedData.specialRequests && (
                <div className="mt-4 p-3 bg-yellow-50 rounded">
                  <p className="font-medium text-yellow-800">Special Requests</p>
                  <p className="text-sm text-yellow-700">{extractedData.specialRequests}</p>
                </div>
              )}

              {/* Missing Fields Warning */}
              {(() => {
                const missingFields = [];
                if (!extractedData.eventName) missingFields.push("event name");
                if (!extractedData.eventDate) missingFields.push("event date");
                if (!extractedData.customerName) missingFields.push("customer name");
                if (!extractedData.customerEmail) missingFields.push("customer email");

                if (missingFields.length > 0) {
                  return (
                    <div className="mt-4 p-3 bg-orange-50 rounded border border-orange-200">
                      <p className="font-medium text-orange-800">Missing Information</p>
                      <p className="text-sm text-orange-700">
                        Please record again and mention: {missingFields.join(", ")}
                      </p>
                    </div>
                  );
                }
                return null;
              })()}

              <div className="flex justify-end gap-3 mt-6">
                <Button variant="outline" onClick={resetForm}>
                  Start Over
                </Button>
                <Button 
                  onClick={confirmBooking}
                  disabled={createBookingFromVoice.isPending}
                  className="bg-purple-600 hover:bg-purple-700"
                >
                  {createBookingFromVoice.isPending ? "Creating..." : "Create Booking"}
                </Button>
              </div>
            </Card>
          )}
        </div>
      </DialogContent>
    </Dialog>
  );
}

