import { 
  BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, 
  AreaChart, Area, PieChart as RechartsPieChart, Pie, Cell, LineChart, Line
} from "recharts";
import { format } from "date-fns";

interface AnalyticsData {
  totalBookings: number;
  revenue: number;
  activeLeads: number;
  utilization: number;
  revenueGrowth: number;
  bookingGrowth: number;
  averageBookingValue: number;
  conversionRate: number;
  proposalConversionRate: number;
  monthlyTrends: Array<{
    month: string;
    bookings: number;
    revenue: number;
    utilization: number;
  }>;
  venuePerformance: Array<{
    name: string;
    bookings: number;
    revenue: number;
    utilization: number;
  }>;
  revenueByEventType: Array<{
    type: string;
    revenue: number;
    count: number;
  }>;
  completedEvents: number;
  cancelledEvents: number;
  cancellationRate: number;
  averageLeadValue: number;
  totalDepositsCollected: number;
  outstandingRevenue: number;
  sentProposals: number;
  acceptedProposals: number;
  totalPayments: number;
  leadSources: Record<string, number>;
  customerTypes: Record<string, number>;
}

interface RevenueAnalytics {
  revenueByStatus: {
    collected: number;
    pending: number;
    outstanding: number;
  };
  paymentBreakdown: {
    deposits: number;
    finalPayments: number;
    refunds: number;
  };
  monthlyRevenue: Array<{
    month: string;
    revenue: number;
    transactions: number;
  }>;
  revenueByCustomerType: Record<string, {
    total: number;
    count: number;
    average: number;
  }>;
  totalRevenue: number;
  projectedRevenue: number;
}

interface CustomerAnalytics {
  totalCustomers: number;
  totalLeads: number;
  acquisitionTrends: Array<{
    month: string;
    customers: number;
    leads: number;
    conversion: number;
  }>;
  customerLTV: Array<{
    id: string;
    name: string;
    type: string;
    totalValue: number;
    bookingCount: number;
    averageBookingValue: number;
  }>;
  leadSources: Record<string, {
    leads: number;
    converted: number;
    revenue: number;
    conversionRate: number;
    averageRevenue: number;
  }>;
}

interface VenueAnalytics {
  venueMetrics: Array<{
    id: string;
    name: string;
    capacity: number;
    totalBookings: number;
    confirmedBookings: number;
    totalRevenue: number;
    averageRevenue: number;
    utilization: number;
    averageGuestCount: number;
  }>;
  eventTypesByVenue: Record<string, Record<string, number>>;
  spaceMetrics: Array<{
    id: string;
    name: string;
    venueName: string;
    capacity: number;
    bookings: number;
    revenue: number;
  }>;
  totalVenues: number;
  totalSpaces: number;
}

const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042', '#8884D8', '#82CA9D'];

export default function Reports() {
  const [dateRange, setDateRange] = useState("3months");
  const [reportType, setReportType] = useState("overview");
  const [autoRefresh, setAutoRefresh] = useState(true);
  const [mobileNavOpen, setMobileNavOpen] = useState(false);
  const [sidebarCollapsed, setSidebarCollapsed] = useState(false);
  const { toast } = useToast();
  const { formatAmount } = useFormattedCurrency();
  const queryClient = useQueryClient();

  // Fetch comprehensive analytics data
  const { data: analyticsData, isLoading: analyticsLoading, refetch: refetchAnalytics } = useQuery<AnalyticsData>({
    queryKey: ["/api/reports/analytics", dateRange],
    refetchInterval: autoRefresh ? 30000 : false,
