              <span className="text-slate-600 font-medium text-sm">JD</span>
            </div>
            <div className="flex-1 min-w-0">
              <p className="text-sm font-medium text-slate-900 truncate">John Doe</p>
              <p className="text-xs text-slate-500 truncate">Venue Manager</p>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

client/src/components/search/global-sear.content:
import { useState, useEffect, useRef } from "react";
import { Search, Calendar, Package, Wrench, X, Clock, MapPin, DollarSign } from "lucide-react";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { useQuery } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useLocation } from "wouter";

interface SearchResult {
  id: string;
  type: 'event' | 'service' | 'package' | 'venue' | 'customer';
  title: string;
  subtitle?: string;
  description?: string;
  metadata?: {
    date?: string;
    price?: number;
    status?: string;
    location?: string;
  };
}

interface GlobalSearchProps {
  isOpen: boolean;
  onClose: () => void;
}

export function GlobalSearch({ isOpen, onClose }: GlobalSearchProps) {
  const [query, setQuery] = useState("");
  const [results, setResults] = useState<SearchResult[]>([]);
  const [isSearching, setIsSearching] = useState(false);
  const [, setLocation] = useLocation();
  const inputRef = useRef<HTMLInputElement>(null);

  // Focus input when modal opens
  useEffect(() => {
    if (isOpen && inputRef.current) {
      inputRef.current.focus();
    }
  }, [isOpen]);

  // Search function with debouncing
  useEffect(() => {
    if (!query.trim()) {
      setResults([]);
      return;
    }

    const searchTimeout = setTimeout(async () => {
      setIsSearching(true);
      try {
        const response = await apiRequest("GET", `/api/search?q=${encodeURIComponent(query)}`);
        const data = await response.json();
        setResults(data.results || []);
      } catch (error) {
        console.error("Search error:", error);
        setResults([]);
      } finally {
        setIsSearching(false);
      }
    }, 300);

    return () => clearTimeout(searchTimeout);
  }, [query]);

  const handleResultClick = (result: SearchResult) => {
    // Navigate to the appropriate page based on result type
    switch (result.type) {
      case 'event':
        setLocation('/events');
        break;
      case 'service':
      case 'package':
        setLocation('/packages');
        break;
      case 'venue':
        setLocation('/venues');
        break;
      case 'customer':
        setLocation('/customers');
        break;
    }
    onClose();
  };

  const getTypeIcon = (type: string) => {
    switch (type) {
      case 'event':
        return <Calendar className="w-4 h-4" />;
      case 'service':
        return <Wrench className="w-4 h-4" />;
      case 'package':
        return <Package className="w-4 h-4" />;
      case 'venue':
        return <MapPin className="w-4 h-4" />;
      case 'customer':
        return <Calendar className="w-4 h-4" />;
      default:
        return <Search className="w-4 h-4" />;
    }
  };

  const getTypeColor = (type: string) => {
    switch (type) {
      case 'event':
        return 'bg-blue-100 text-blue-800';
      case 'service':
        return 'bg-green-100 text-green-800';
      case 'package':
        return 'bg-purple-100 text-purple-800';
      case 'venue':
        return 'bg-orange-100 text-orange-800';
      case 'customer':
        return 'bg-gray-100 text-gray-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="sm:max-w-[600px] max-h-[80vh] overflow-hidden p-0">
        <DialogHeader className="px-6 py-4 border-b">
          <DialogTitle className="flex items-center gap-2">
            <Search className="w-5 h-5" />
            Search Everything
          </DialogTitle>
        </DialogHeader>

        <div className="px-6 py-4">
          <div className="relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-4 h-4" />
            <Input
              ref={inputRef}
              placeholder="Search events, services, packages, venues, customers..."
              value={query}
              onChange={(e) => setQuery(e.target.value)}
              className="pl-10 pr-10"
              onKeyDown={(e) => {
                if (e.key === 'Escape') {
                  onClose();
                }
              }}
            />
            {query && (
              <Button
                variant="ghost"
                size="sm"
                className="absolute right-1 top-1/2 transform -translate-y-1/2 h-6 w-6 p-0"
                onClick={() => setQuery("")}
              >
                <X className="w-3 h-3" />
              </Button>
            )}
          </div>
        </div>

        <div className="flex-1 overflow-y-auto px-6 pb-6">
          {isSearching && (
            <div className="flex items-center justify-center py-8">
              <div className="animate-spin w-6 h-6 border-2 border-blue-600 border-t-transparent rounded-full" />
            </div>
          )}

          {!isSearching && query && results.length === 0 && (
            <div className="text-center py-8 text-gray-500">
              <Search className="w-12 h-12 mx-auto mb-4 text-gray-300" />
              <p>No results found for "{query}"</p>
              <p className="text-sm mt-1">Try searching for events, services, packages, or venues</p>
            </div>
          )}

          {!isSearching && results.length > 0 && (
            <div className="space-y-2">
              <div className="text-sm text-gray-500 mb-3">
                Found {results.length} result{results.length !== 1 ? 's' : ''}
              </div>
              {results.map((result) => (
                <Card 
                  key={result.id} 
                  className="cursor-pointer hover:shadow-md transition-shadow"
                  onClick={() => handleResultClick(result)}
                >
                  <CardContent className="p-4">
                    <div className="flex items-start justify-between">
                      <div className="flex-1">
                        <div className="flex items-center gap-2 mb-1">
                          <Badge variant="secondary" className={getTypeColor(result.type)}>
                            <span className="flex items-center gap-1">
                              {getTypeIcon(result.type)}
                              {result.type}
                            </span>
                          </Badge>
                        </div>
                        <h3 className="font-medium text-gray-900 mb-1">
                          {result.title}
                        </h3>
                        {result.subtitle && (
                          <p className="text-sm text-gray-600 mb-1">
                            {result.subtitle}
                          </p>
                        )}
                        {result.description && (
                          <p className="text-sm text-gray-500">
                            {result.description}
                          </p>
                        )}
                        {result.metadata && (
                          <div className="flex items-center gap-4 mt-2 text-xs text-gray-500">
                            {result.metadata.date && (
                              <div className="flex items-center gap-1">
                                <Clock className="w-3 h-3" />
                                {result.metadata.date}
                              </div>
                            )}
                            {result.metadata.price && (
                              <div className="flex items-center gap-1">
                                <DollarSign className="w-3 h-3" />
                                ${result.metadata.price.toLocaleString()}
                              </div>
                            )}
                            {result.metadata.location && (
                              <div className="flex items-center gap-1">
                                <MapPin className="w-3 h-3" />
                                {result.metadata.location}
                              </div>
                            )}
                            {result.metadata.status && (
                              <Badge variant="outline" className="text-xs">
                                {result.metadata.status}
                              </Badge>
                            )}
                          </div>
                        )}
                      </div>
                    </div>
                  </CardContent>
                </Card>
              ))}
            </div>
          )}

          {!query && (
            <div className="space-y-4">
              <div className="text-sm text-gray-500 mb-3">
                Quick Search Tips
              </div>
              <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
                <div className="flex items-center gap-3 p-3 bg-gray-50 rounded-lg">
                  <Calendar className="w-8 h-8 text-blue-600" />
                  <div>
                    <div className="font-medium text-sm">Events</div>
                    <div className="text-xs text-gray-500">Search by name, date, or type</div>
                  </div>
                </div>
                <div className="flex items-center gap-3 p-3 bg-gray-50 rounded-lg">
                  <Package className="w-8 h-8 text-purple-600" />
                  <div>
                    <div className="font-medium text-sm">Packages</div>
                    <div className="text-xs text-gray-500">Find event packages and pricing</div>
                  </div>
                </div>
                <div className="flex items-center gap-3 p-3 bg-gray-50 rounded-lg">
                  <Wrench className="w-8 h-8 text-green-600" />
                  <div>
                    <div className="font-medium text-sm">Services</div>
                    <div className="text-xs text-gray-500">Search available services</div>
                  </div>
                </div>
                <div className="flex items-center gap-3 p-3 bg-gray-50 rounded-lg">
                  <MapPin className="w-8 h-8 text-orange-600" />
                  <div>
                    <div className="font-medium text-sm">Venues</div>
                    <div className="text-xs text-gray-500">Find venue spaces and capacity</div>
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/ai/email-reply-mod.content:
import { useState } from "react";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Textarea } from "@/components/ui/textarea";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Mail, Copy, Send, AlertTriangle, CheckCircle } from "lucide-react";
import { useMutation } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

interface Props {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  customerEmail?: string;
  customerName?: string;
  initialMessage?: string;
}

export function EmailReplyModal({ open, onOpenChange, customerEmail, customerName, initialMessage }: Props) {
  const { toast } = useToast();
  const [formData, setFormData] = useState({
    customerMessage: initialMessage || "",
    context: "",
    customerData: {
      name: customerName || "",
      email: customerEmail || "",
      company: "",
      eventType: "",
      guestCount: "",
      budget: "",
      eventDate: ""
    }
  });

  const generateEmailReply = useMutation({
    mutationFn: async (data: any) => {
      const response = await apiRequest("POST", "/api/ai/email-reply", data);
      return response.json();
    },
    onSuccess: (data) => {
      toast({
        title: "Email Reply Generated",
        description: `AI generated reply with ${data.confidence}% confidence`
      });
    }
  });

  const handleGenerate = () => {
    if (!formData.customerMessage.trim()) {
      toast({
        title: "Missing Information",
        description: "Please enter the customer's message",
        variant: "destructive"
      });
      return;
    }
    generateEmailReply.mutate(formData);
  };

  const handleCopyToClipboard = (text: string) => {
    navigator.clipboard.writeText(text);
    toast({
      title: "Copied to Clipboard",
      description: "Email content copied successfully"
    });
  };

  const replyData = generateEmailReply.data;

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="max-w-5xl max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <Mail className="h-5 w-5 text-blue-600" />
            Auto Email Replies
            <Badge variant="secondary" className="bg-purple-100 text-purple-700">AI</Badge>
          </DialogTitle>
        </DialogHeader>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          {/* Input Form */}
          <div className="space-y-4">
            <Card>
              <CardHeader>
                <CardTitle className="text-lg">Customer Message</CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div>
                  <Label>Customer's Email/Message</Label>
                  <Textarea
                    value={formData.customerMessage}
                    onChange={(e) => setFormData(prev => ({...prev, customerMessage: e.target.value}))}
                    placeholder="Paste the customer's inquiry here..."
                    rows={4}
                  />
                </div>

                <div>
                  <Label>Additional Context</Label>
                  <Textarea
                    value={formData.context}
                    onChange={(e) => setFormData(prev => ({...prev, context: e.target.value}))}
                    placeholder="Any additional context about the inquiry, venue availability, special offers, etc."
                    rows={3}
                  />
                </div>
              </CardContent>
            </Card>

            <Card>
              <CardHeader>
                <CardTitle className="text-lg">Customer Information</CardTitle>
              </CardHeader>
              <CardContent className="space-y-3">
                <div className="grid grid-cols-2 gap-3">
                  <div>
                    <Label className="text-sm">Name</Label>
                    <Input
                      value={formData.customerData.name}
                      onChange={(e) => setFormData(prev => ({
                        ...prev,
                        customerData: {...prev.customerData, name: e.target.value}
                      }))}
                      placeholder="Customer name"
                    />
                  </div>
                  <div>
                    <Label className="text-sm">Email</Label>
                    <Input
                      value={formData.customerData.email}
                      onChange={(e) => setFormData(prev => ({
                        ...prev,
                        customerData: {...prev.customerData, email: e.target.value}
                      }))}
                      placeholder="customer@email.com"
                    />
                  </div>
                </div>

                <div className="grid grid-cols-2 gap-3">
                  <div>
                    <Label className="text-sm">Event Type</Label>
                    <Input
                      value={formData.customerData.eventType}
                      onChange={(e) => setFormData(prev => ({
                        ...prev,
                        customerData: {...prev.customerData, eventType: e.target.value}
                      }))}
                      placeholder="Wedding, Corporate, etc."
                    />
                  </div>
                  <div>
                    <Label className="text-sm">Guest Count</Label>
                    <Input
                      value={formData.customerData.guestCount}
                      onChange={(e) => setFormData(prev => ({
                        ...prev,
                        customerData: {...prev.customerData, guestCount: e.target.value}
                      }))}
                      placeholder="50"
                    />
                  </div>
                </div>

                <Button 
                  onClick={handleGenerate}
                  disabled={generateEmailReply.isPending || !formData.customerMessage.trim()}
                  className="w-full"
                >
                  {generateEmailReply.isPending ? "Generating Reply..." : "Generate AI Reply"}
                </Button>
              </CardContent>
            </Card>

            {/* AI Disclaimer */}
            <Card className="border-amber-200 bg-amber-50">
              <CardContent className="p-4">
                <div className="flex items-start gap-3">
                  <AlertTriangle className="h-5 w-5 text-amber-600 mt-0.5" />
                  <div>
                    <p className="font-medium text-amber-800">AI-Generated Content</p>
                    <p className="text-sm text-amber-700">
                      Please review and customize the AI-generated reply before sending. Verify all details and pricing information.
                    </p>
                  </div>
                </div>
              </CardContent>
            </Card>
          </div>

          {/* Generated Reply */}
          <div className="space-y-4">
            {replyData && (
              <>
                <Card>
                  <CardHeader>
                    <CardTitle className="text-lg flex items-center justify-between">
                      Generated Reply
                      <div className="flex items-center gap-2">
                        <Badge variant="outline" className="text-xs">
                          {replyData.confidence}% confidence
                        </Badge>
                        <Badge variant="outline" className="text-xs capitalize">
                          {replyData.tone}
                        </Badge>
                      </div>
                    </CardTitle>
                  </CardHeader>
                  <CardContent className="space-y-4">
                    <div>
                      <Label className="text-sm font-medium">Subject Line</Label>
                      <div className="mt-1 p-3 bg-gray-50 rounded border relative group">
                        <p className="font-medium">{replyData.subject}</p>
                        <Button
                          variant="ghost"
                          size="sm"
                          className="absolute top-1 right-1 opacity-0 group-hover:opacity-100"
                          onClick={() => handleCopyToClipboard(replyData.subject)}
                        >
                          <Copy className="h-4 w-4" />
                        </Button>
                      </div>
                    </div>

                    <div>
                      <Label className="text-sm font-medium">Email Body</Label>
                      <div className="mt-1 p-4 bg-gray-50 rounded border relative group">
                        <div className="whitespace-pre-wrap">{replyData.body}</div>
                        <Button
                          variant="ghost"
                          size="sm"
                          className="absolute top-2 right-2 opacity-0 group-hover:opacity-100"
                          onClick={() => handleCopyToClipboard(replyData.body)}
                        >
                          <Copy className="h-4 w-4" />
                        </Button>
                      </div>
                    </div>

                    <div className="flex gap-2">
                      <Button 
                        onClick={() => handleCopyToClipboard(`Subject: ${replyData.subject}\n\n${replyData.body}`)}
                        variant="outline"
                        className="flex-1"
                      >
                        <Copy className="h-4 w-4 mr-2" />
                        Copy Full Email
                      </Button>
                      <Button className="flex-1">
                        <Send className="h-4 w-4 mr-2" />
                        Open in Email Client
                      </Button>
                    </div>
                  </CardContent>
                </Card>

                {/* Next Steps */}
                {replyData.nextSteps?.length > 0 && (
                  <Card>
                    <CardHeader>
                      <CardTitle className="text-lg">Recommended Next Steps</CardTitle>
                    </CardHeader>
                    <CardContent>
                      <ul className="space-y-2">
                        {replyData.nextSteps.map((step: string, index: number) => (
                          <li key={index} className="flex items-start gap-2 text-sm">
                            <CheckCircle className="h-4 w-4 text-green-500 mt-0.5" />
                            {step}
                          </li>
                        ))}
                      </ul>
                    </CardContent>
                  </Card>
                )}

                {/* Follow-up Suggestion */}
                {replyData.suggestedFollowUp && (
                  <Card>
                    <CardHeader>
                      <CardTitle className="text-lg">Follow-up Reminder</CardTitle>
                    </CardHeader>
                    <CardContent>
                      <p className="text-sm text-gray-700">{replyData.suggestedFollowUp}</p>
                    </CardContent>
                  </Card>
                )}
              </>
            )}
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

