                                  <div className="space-y-1">
                                    {comm.direction === 'outbound' ? (
                                      <>
                                        <div><span className="font-medium text-gray-700">From:</span> {comm.sentBy || comm.sender || 'Venue Team'}</div>
                                        <div><span className="font-medium text-gray-700">To:</span> {comm.recipient || 'Customer'}</div>
                                      </>
                                    ) : (
                                      <>
                                        <div><span className="font-medium text-gray-700">From:</span> {comm.sender || comm.sentBy || 'Customer'}</div>
                                        <div><span className="font-medium text-gray-700">To:</span> {comm.recipient || 'Venue Team'}</div>
                                      </>
                                    )}
                                  </div>

                                  {/* Timing & Status */}
                                  <div className="space-y-1">
                                    <div><span className="font-medium text-gray-700">Status:</span> 
                                      <Badge variant={comm.status === 'failed' ? 'destructive' : comm.direction === 'inbound' ? 'secondary' : 'outline'} className="ml-1 text-xs">
                                        {comm.status === 'failed' ? 'Failed' : 
                                         comm.status === 'received' ? 'Received' :
                                         comm.status === 'delivered' ? 'Delivered' :
                                         comm.status || (comm.direction === 'inbound' ? 'Received' : 'Sent')}
                                      </Badge>
                                    </div>
                                    {(comm.sentAt || comm.createdAt) && (
                                      <div><span className="font-medium text-gray-700">
                                        {comm.direction === 'outbound' ? 'Sent' : 'Received'}:</span> {
                                        comm.sentAt 
                                          ? format(new Date(comm.sentAt), "MMM d, yyyy 'at' h:mm a")
                                          : format(new Date(comm.createdAt), "MMM d, yyyy 'at' h:mm a")
                                      }</div>
                                    )}
                                    {comm.direction === 'outbound' && comm.deliveredAt && (
                                      <div><span className="font-medium text-gray-700">Delivered:</span> {format(new Date(comm.deliveredAt), "h:mm a")}</div>
                                    )}
                                  </div>
                                </div>

                                {/* Attachments (only show for outbound communications for security) */}
                                {comm.direction === 'outbound' && comm.attachments && comm.attachments.length > 0 && (
                                  <div className="bg-blue-50 border-blue-200 p-3 rounded border">
                                    <div className="text-xs font-medium mb-2 text-blue-700">
                                      Attachments Sent ({comm.attachments.length}):
                                    </div>
                                    <div className="space-y-1">
                                      {comm.attachments.map((attachment: any, idx: number) => (
                                        <div key={idx} className="flex items-center gap-2 text-xs">
                                          <Paperclip className="h-3 w-3 text-blue-500" />
                                          <span className="text-blue-700">
                                            {attachment.name || `Attachment ${idx + 1}`}
                                          </span>
                                          {attachment.size && (
                                            <span className="text-gray-500">({Math.round(attachment.size / 1024)} KB)</span>
                                          )}
                                        </div>
                                      ))}
                                    </div>
                                  </div>
                                )}

                                {/* Security notice for inbound communications */}
                                {comm.direction === 'inbound' && (
                                  <div className="bg-amber-50 border-amber-200 p-2 rounded border">
                                    <div className="text-xs text-amber-700 flex items-center gap-1">
                                      <Shield className="h-3 w-3" />
                                      Customer attachments are not displayed for security reasons
                                    </div>
                                  </div>
                                )}

                                {/* Email tracking info for outbound emails */}
                                {comm.direction === 'outbound' && comm.type === 'email' && (
                                  <div className="bg-blue-50 p-2 rounded border border-blue-200">
                                    <div className="text-xs text-blue-600">
                                      <Clock className="h-3 w-3 inline mr-1" />
                                      Email tracking: {(comm.openCount || 0) > 0 
                                        ? `Opened ${comm.openCount} time${(comm.openCount || 0) > 1 ? 's' : ''}`
                                        : 'Not yet opened'
                                      }
                                      {comm.lastOpenedAt && (
                                        <span className="block mt-1">
                                          Last opened: {format(new Date(comm.lastOpenedAt), "MMM d 'at' h:mm a")}
                                        </span>
                                      )}
                                    </div>
                                  </div>
                                )}
                              </div>
                            ) : (
                              <div 
                                className="cursor-pointer hover:text-gray-800"
                                onClick={toggleExpanded}
                              >
                                {(() => {
                                  const content = comm.message || comm.content;
                                  let displayContent = preview || content;

                                  // If content contains HTML, extract clean text for preview
                                  if (displayContent && displayContent.includes('<') && displayContent.includes('>')) {
                                    try {
                                      const tempDiv = document.createElement('div');
                                      tempDiv.innerHTML = displayContent;
                                      const scripts = tempDiv.querySelectorAll('script, style');
                                      scripts.forEach(el => el.remove());
                                      const cleanText = tempDiv.textContent || tempDiv.innerText || '';
                                      displayContent = cleanText.replace(/\s+/g, ' ').trim();
                                    } catch (error) {
                                      // Fallback to original content if parsing fails
                                    }
                                  }

                                  const truncated = displayContent?.substring(0, 100) + (displayContent?.length > 100 ? '...' : '');
                                  return truncated;
                                })()}
                                {(comm.message || comm.content) && (comm.message || comm.content).length > 100 && (
                                  <span className="text-blue-500 ml-1 text-xs">Click to expand</span>
                                )}
                              </div>
                            )}
                          </div>
                        </div>
                      );
                    })
                  )}
                </div>
              </CardContent>
            </Card>
          </div>

          {/* Right Column - Customer & Communication */}
          <div className="lg:col-span-2 space-y-6">
            {/* Customer Information */}
            <Card>
              <CardHeader>
                <CardTitle className="text-lg">Customer</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="space-y-2 text-sm">
                  <div className="font-medium">{customer?.name || 'Customer Name'}</div>
                  <div className="text-gray-600">{customer?.email || 'No email'}</div>
                  <div className="text-gray-600">{customer?.phone || 'No phone'}</div>
                  {customer?.company && (
                    <div className="text-gray-600">{customer.company}</div>
                  )}
                  {customer?.address && (
                    <div className="text-gray-600 text-xs">{customer.address}</div>
                  )}
                </div>
              </CardContent>
            </Card>

            {/* Quick Actions */}
            <Card>
              <CardHeader>
                <CardTitle className="text-lg">Quick Actions</CardTitle>
              </CardHeader>
              <CardContent className="space-y-3">
                <Button 
                  variant="outline" 
                  className="w-full"
                  onClick={handleResendProposal}
                  disabled={resendProposalMutation.isPending}
                >
                  <Send className="h-4 w-4 mr-2" />
                  {resendProposalMutation.isPending ? 'Sending...' : 'Resend Proposal'}
                </Button>


              </CardContent>
            </Card>

            {/* Enhanced Communication Section */}
            <Card className="h-fit">
              <CardHeader>
                <CardTitle className="text-lg flex items-center gap-2">
                  <MessageSquare className="h-5 w-5" />
                  Customer Communication
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                {/* Message Type Selection */}
                <div>
                  <Label htmlFor="messageType">Message Type</Label>
                  <select
                    id="messageType"
                    value={messageType}
                    onChange={(e) => {
                      setMessageType(e.target.value);
                      if (e.target.value === 'email') {
                        setShowEmailComposer(true);
                      } else {
                        setShowEmailComposer(false);
                      }
                    }}
                    className="w-full mt-1 p-2 border rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  >
                    <option value="email">ðŸ“§ Email Customer</option>
                    <option value="note">ðŸ“ Internal Note</option>
                  </select>
                </div>

                {/* Email Subject (only for emails) */}
                {messageType === 'email' && (
                  <div>
                    <Label htmlFor="emailSubject">Subject Line</Label>
                    <input
                      id="emailSubject"
                      type="text"
                      value={emailSubject}
                      onChange={(e) => setEmailSubject(e.target.value)}
                      placeholder="Re: Your Event Proposal"
                      className="w-full mt-1 p-2 border rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    />
                  </div>
                )}

                {/* Message Content */}
                <div>
                  <Label htmlFor="message">
                    {messageType === 'email' ? 'Email Content' : 'Note Content'}
                  </Label>
                  <Textarea
                    id="message"
                    value={newMessage}
                    onChange={(e) => setNewMessage(e.target.value)}
                    placeholder={messageType === 'email' 
                      ? "Hi [Customer Name],\n\nI wanted to follow up on your event proposal...\n\nBest regards,\nYour Name" 
                      : "Internal note about this proposal..."}
                    rows={messageType === 'email' ? 8 : 4}
                    className="w-full resize-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  />
                  <div className="text-xs text-gray-500 mt-1">
                    {newMessage.length}/2000 characters
                  </div>
                </div>

                {/* File Attachments (only for emails) */}
                {messageType === 'email' && (
                  <div>
                    <Label>File Attachments</Label>
                    <div className="mt-2 space-y-2">
                      <input
                        type="file"
                        multiple
                        onChange={handleFileAttachment}
                        className="hidden"
                        id="file-attachment"
                        accept=".pdf,.doc,.docx,.jpg,.jpeg,.png,.gif"
                      />
                      <label
                        htmlFor="file-attachment"
                        className="flex items-center gap-2 p-2 border-2 border-dashed border-gray-300 rounded-md cursor-pointer hover:border-blue-500 hover:bg-blue-50 transition-colors"
                      >
                        <Paperclip className="h-4 w-4 text-gray-500" />
                        <span className="text-sm text-gray-600">
                          Click to attach files (PDF, DOC, JPG, PNG)
                        </span>
                      </label>

                      {/* Attachment List */}
                      {attachments.length > 0 && (
                        <div className="space-y-1">
                          {attachments.map((file, index) => (
                            <div key={index} className="flex items-center justify-between p-2 bg-gray-50 rounded">
                              <div className="flex items-center gap-2">
                                <FileText className="h-4 w-4 text-gray-500" />
                                <span className="text-sm text-gray-700">{file.name}</span>
                                <span className="text-xs text-gray-500">
                                  ({(file.size / 1024).toFixed(1)} KB)
                                </span>
                              </div>
                              <button
                                onClick={() => removeAttachment(index)}
                                className="text-red-500 hover:text-red-700"
                              >
                                <X className="h-4 w-4" />
                              </button>
                            </div>
                          ))}
                        </div>
                      )}
                    </div>
                  </div>
                )}

                {/* Send Button */}
                <Button
                  onClick={handleSendMessage}
                  disabled={!newMessage.trim() || sendMessageMutation.isPending}
                  className="w-full bg-gradient-to-r from-blue-600 to-blue-700 hover:from-blue-700 hover:to-blue-800"
                  size="lg"
                >
                  <Send className="h-4 w-4 mr-2" />
                  {sendMessageMutation.isPending 
                    ? 'Sending...' 
                    : `Send ${messageType === 'email' ? 'Email' : 'Note'}`
                  }
                </Button>

                {messageType === 'email' && (
                  <div className="text-xs text-gray-500 bg-blue-50 p-2 rounded">
                    ðŸ’¡ <strong>Tip:</strong> Professional email communication is automatically tracked in the communication history below.
                  </div>
                )}
              </CardContent>
            </Card>
          </div>
        </div>
      </DialogContent>


    </Dialog>
  );
}

client/src/components/voice/voice-bookin.content:
import { useState, useRef, useEffect } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Textarea } from "@/components/ui/textarea";
import { 
  Mic, 
  MicOff, 
  Phone, 
  PhoneOff, 
  MessageSquare, 
  AlertTriangle,
  CheckCircle,
  Clock,
  User,
  Calendar,
  MapPin,
  Users,
  DollarSign
} from "lucide-react";
import { useMutation } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

interface VoiceBookingPanelProps {
  onBookingDataExtracted: (data: any) => void;
  isCallMode?: boolean;
}

export function VoiceBookingPanel({ onBookingDataExtracted, isCallMode = false }: VoiceBookingPanelProps) {
  const { toast } = useToast();
  const [isRecording, setIsRecording] = useState(false);
  const [transcript, setTranscript] = useState("");
  const [isProcessing, setIsProcessing] = useState(false);
  const [extractedData, setExtractedData] = useState<any>(null);
  const [callDuration, setCallDuration] = useState(0);
  const mediaRecorderRef = useRef<MediaRecorder | null>(null);
  const recognitionRef = useRef<any>(null);
  const callTimerRef = useRef<NodeJS.Timeout | null>(null);

  const parseVoiceData = useMutation({
    mutationFn: async (voiceText: string) => {
      const response = await apiRequest("POST", "/api/ai/parse-voice", {
        transcript: voiceText,
        context: isCallMode ? "customer_call" : "voice_booking"
      });
      return response.json();
    },
    onSuccess: (data) => {
      setExtractedData(data);
      onBookingDataExtracted(data);
      toast({
        title: "Voice Data Processed",
        description: `Extracted booking details for ${data.eventName || 'new event'}`
      });
    },
    onError: () => {
      toast({
        title: "Processing Failed",
        description: "Could not process voice input. Please try again.",
        variant: "destructive"
      });
    }
  });

  useEffect(() => {
    if (isCallMode && isRecording) {
      callTimerRef.current = setInterval(() => {
        setCallDuration(prev => prev + 1);
      }, 1000);
    } else {
      if (callTimerRef.current) {
        clearInterval(callTimerRef.current);
      }
    }

    return () => {
      if (callTimerRef.current) {
        clearInterval(callTimerRef.current);
      }
    };
  }, [isCallMode, isRecording]);

  const startRecording = async () => {
    try {
      // Start audio recording for call transcription
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      mediaRecorderRef.current = new MediaRecorder(stream);

      // Start voice recognition
      if ('webkitSpeechRecognition' in window || 'SpeechRecognition' in window) {
        const SpeechRecognition = (window as any).webkitSpeechRecognition || (window as any).SpeechRecognition;
        recognitionRef.current = new SpeechRecognition();
        recognitionRef.current.continuous = true;
        recognitionRef.current.interimResults = true;
        recognitionRef.current.lang = 'en-US';

        recognitionRef.current.onresult = (event: any) => {
          let finalTranscript = '';
          let interimTranscript = '';

          for (let i = event.resultIndex; i < event.results.length; i++) {
            const transcript = event.results[i][0].transcript;
            if (event.results[i].isFinal) {
              finalTranscript += transcript;
            } else {
              interimTranscript += transcript;
            }
          }

          setTranscript(prev => prev + finalTranscript);
        };

        recognitionRef.current.start();
      }

      setIsRecording(true);
      setCallDuration(0);

      toast({
        title: isCallMode ? "Call Recording Started" : "Voice Booking Started",
        description: isCallMode ? "Recording customer call..." : "Listening for booking details..."
      });

    } catch (error) {
      toast({
        title: "Recording Failed",
        description: "Could not access microphone. Please check permissions.",
        variant: "destructive"
      });
    }
  };

  const stopRecording = () => {
    if (recognitionRef.current) {
      recognitionRef.current.stop();
    }

    if (mediaRecorderRef.current) {
      mediaRecorderRef.current.stop();
      mediaRecorderRef.current.stream.getTracks().forEach(track => track.stop());
    }

    setIsRecording(false);

    if (transcript.trim()) {
      setIsProcessing(true);
      parseVoiceData.mutate(transcript);
    }

    toast({
      title: isCallMode ? "Call Ended" : "Recording Stopped",
      description: "Processing voice data..."
    });
  };

  const formatTime = (seconds: number) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  const clearSession = () => {
    setTranscript("");
    setExtractedData(null);
    setCallDuration(0);
    setIsProcessing(false);
  };

  return (
    <div className="space-y-4">
      {/* Voice Instructions */}
      <Card className="border-blue-200 bg-blue-50">
        <CardHeader>
          <CardTitle className="text-lg flex items-center gap-2">
            <MessageSquare className="h-5 w-5 text-blue-600" />
            {isCallMode ? "Call Capture Instructions" : "Voice Booking Instructions"}
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="text-sm text-blue-800 space-y-2">
            <p className="font-medium">
              {isCallMode 
                ? "When a customer calls, start recording to capture their requirements:"
                : "Speak clearly and include these details for AI to create your booking:"
              }
            </p>
            <ul className="list-disc list-inside space-y-1 text-blue-700">
              <li><strong>Event Type:</strong> "Corporate event", "Wedding", "Birthday party", etc.</li>
              <li><strong>Customer Info:</strong> "Customer name is John Smith, email john@company.com"</li>
              <li><strong>Date & Time:</strong> "Event on March 15th at 6 PM" or "Saturday evening"</li>
              <li><strong>Guest Count:</strong> "Expecting 50 guests" or "About 100 people"</li>
              <li><strong>Venue Preference:</strong> "Grand Ballroom" or "Main hall"</li>
              <li><strong>Services:</strong> "Need catering and sound system"</li>
              <li><strong>Budget:</strong> "Budget around $5000" (optional)</li>
            </ul>
            <p className="text-xs mt-3 font-medium">
              {isCallMode 
                ? "The AI will capture everything the customer says and suggest creating an event."
                : "Example: 'Corporate event for John Smith, john@company.com, March 15th at 6 PM, 50 guests, Grand Ballroom, need catering'"
              }
            </p>
          </div>
        </CardContent>
      </Card>

      {/* Recording Controls */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center justify-between">
            <div className="flex items-center gap-2">
              {isCallMode ? <Phone className="h-5 w-5" /> : <Mic className="h-5 w-5" />}
              {isCallMode ? "Customer Call" : "Voice Booking"}
            </div>
            {isRecording && (
              <div className="flex items-center gap-2">
                <div className="w-3 h-3 bg-red-500 rounded-full animate-pulse"></div>
                <Badge variant="destructive">
                  {isCallMode ? "RECORDING CALL" : "LISTENING"}
                  {isCallMode && ` - ${formatTime(callDuration)}`}
                </Badge>
              </div>
            )}
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          {/* Recording Button */}
          <div className="flex justify-center">
            {!isRecording ? (
              <Button
                onClick={startRecording}
                size="lg"
                className={`${isCallMode 
                  ? 'bg-green-600 hover:bg-green-700' 
                  : 'bg-blue-600 hover:bg-blue-700'
                } px-8`}
              >
                {isCallMode ? (
                  <>
                    <Phone className="w-5 h-5 mr-2" />
                    Start Call Recording
                  </>
                ) : (
                  <>
                    <Mic className="w-5 h-5 mr-2" />
                    Start Voice Booking
                  </>
                )}
              </Button>
            ) : (
              <Button
                onClick={stopRecording}
                size="lg"
                variant="destructive"
                className="px-8"
              >
                {isCallMode ? (
                  <>
                    <PhoneOff className="w-5 h-5 mr-2" />
                    End Call
                  </>
                ) : (
                  <>
                    <MicOff className="w-5 h-5 mr-2" />
                    Stop Recording
                  </>
                )}
              </Button>
            )}
          </div>

          {/* Live Transcript */}
          {(isRecording || transcript) && (
            <div>
              <h4 className="font-medium mb-2 flex items-center gap-2">
                <MessageSquare className="w-4 h-4" />
                {isCallMode ? "Customer Conversation" : "Voice Transcript"}
              </h4>
              <Textarea
                value={transcript}
                onChange={(e) => setTranscript(e.target.value)}
                placeholder={isCallMode 
                  ? "Customer conversation will appear here as they speak..."
                  : "Your voice input will appear here..."
                }
                rows={6}
                className="font-mono text-sm"
              />
            </div>
          )}

          {/* Processing Status */}
          {isProcessing && (
            <div className="flex items-center gap-2 text-blue-600">
              <Clock className="w-4 h-4 animate-spin" />
              <span className="text-sm">Processing voice input with AI...</span>
            </div>
          )}

          {/* Extracted Data Preview */}
          {extractedData && (
            <Card className="border-green-200 bg-green-50">
              <CardHeader>
                <CardTitle className="text-lg flex items-center gap-2">
                  <CheckCircle className="h-5 w-5 text-green-600" />
                  Extracted Booking Details
                </CardTitle>
              </CardHeader>
              <CardContent>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4 text-sm">
                  {extractedData.eventName && (
                    <div className="flex items-center gap-2">
                      <Calendar className="w-4 h-4 text-gray-500" />
                      <span><strong>Event:</strong> {extractedData.eventName}</span>
                    </div>
                  )}
                  {extractedData.customerName && (
                    <div className="flex items-center gap-2">
                      <User className="w-4 h-4 text-gray-500" />
                      <span><strong>Customer:</strong> {extractedData.customerName}</span>
                    </div>
                  )}
                  {extractedData.eventDate && (
                    <div className="flex items-center gap-2">
                      <Clock className="w-4 h-4 text-gray-500" />
                      <span><strong>Date:</strong> {extractedData.eventDate}</span>
                    </div>
                  )}
                  {extractedData.guestCount && (
                    <div className="flex items-center gap-2">
                      <Users className="w-4 h-4 text-gray-500" />
                      <span><strong>Guests:</strong> {extractedData.guestCount}</span>
                    </div>
                  )}
                  {extractedData.venue && (
                    <div className="flex items-center gap-2">
                      <MapPin className="w-4 h-4 text-gray-500" />
                      <span><strong>Venue:</strong> {extractedData.venue}</span>
                    </div>
                  )}
                  {extractedData.budget && (
                    <div className="flex items-center gap-2">
                      <DollarSign className="w-4 h-4 text-gray-500" />
                      <span><strong>Budget:</strong> {extractedData.budget}</span>
                    </div>
                  )}
                </div>

                {extractedData.services && extractedData.services.length > 0 && (
                  <div className="mt-3">
                    <strong className="text-sm">Services Mentioned:</strong>
                    <div className="flex flex-wrap gap-1 mt-1">
                      {extractedData.services.map((service: string, index: number) => (
                        <Badge key={index} variant="outline" className="text-xs">
                          {service}
                        </Badge>
                      ))}
                    </div>
                  </div>
                )}

                <div className="mt-4 pt-3 border-t border-green-200">
                  <Button
                    onClick={() => onBookingDataExtracted(extractedData)}
                    className="w-full bg-green-600 hover:bg-green-700"
                  >
                    {isCallMode ? "Create Event from Call" : "Open Booking Form"}
                  </Button>
                </div>
              </CardContent>
            </Card>
          )}

          {/* Action Buttons */}
          {(transcript || extractedData) && (
            <div className="flex gap-2">
              <Button
                onClick={clearSession}
                variant="outline"
                className="flex-1"
              >
                Clear Session
              </Button>
              {transcript && !isProcessing && !extractedData && (
                <Button
                  onClick={() => {
                    setIsProcessing(true);
                    parseVoiceData.mutate(transcript);
                  }}
                  className="flex-1"
                >
                  Process Voice Input
                </Button>
              )}
            </div>
          )}
        </CardContent>
      </Card>

      {/* AI Disclaimer */}
      <Card className="border-amber-200 bg-amber-50">
        <CardContent className="p-4">
          <div className="flex items-start gap-3">
            <AlertTriangle className="h-5 w-5 text-amber-600 mt-0.5" />
            <div>
              <p className="font-medium text-amber-800">Voice Recognition Notice</p>
              <p className="text-sm text-amber-700">
                Voice recognition accuracy may vary. Always review and verify extracted details before creating bookings.
                {isCallMode && " Call recordings are processed locally and not stored permanently."}
              </p>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

