import { useState, useEffect } from "react";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Badge } from "@/components/ui/badge";
import { Checkbox } from "@/components/ui/checkbox";
import { Separator } from "@/components/ui/separator";
import { Save, X, Grid3X3, Settings } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { queryClient } from "@/lib/queryClient";


interface EditSpaceModalProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  space: any;
  venueId: string;
  venueName?: string;
}

const SETUP_STYLES = [
  { value: 'round-tables', label: 'Round Tables', description: 'Perfect for dining and networking events' },
  { value: 'u-shape', label: 'U-Shape', description: 'Great for presentations and discussions' },
  { value: 'classroom', label: 'Classroom', description: 'Ideal for training and educational events' },
  { value: 'theater', label: 'Theater', description: 'Best for presentations and performances' },
  { value: 'cocktail', label: 'Cocktail', description: 'Standing reception style setup' },
  { value: 'banquet', label: 'Banquet', description: 'Formal dining with long tables' },
  { value: 'conference', label: 'Conference', description: 'Professional meeting setup' },
  { value: 'custom', label: 'Custom', description: 'Design your own unique layout' },
];

export function EditSpaceModal({ open, onOpenChange, space, venueId, venueName }: EditSpaceModalProps) {
  const { toast } = useToast();
  const [activeTab, setActiveTab] = useState("details");
  const [isSaving, setIsSaving] = useState(false);

  const [formData, setFormData] = useState({
    name: '',
    description: '',
    capacity: '',
    spaceType: 'hall',
    features: '',
    amenities: [] as string[],
    availableSetupStyles: [] as string[],
  });

  const isCreating = !space?.id;

  useEffect(() => {
    if (open) {
      if (space && space.id) {
        // Editing existing space
        console.log('Loading space data:', space);
        setFormData({
          name: space.name || '',
          description: space.description || '',
          capacity: space.capacity?.toString() || '',
          spaceType: space.spaceType || 'hall',
          features: space.features || '',
          amenities: space.amenities || [],
          availableSetupStyles: space.availableSetupStyles || [],
        });
      } else {
        // Creating new space
        setFormData({
          name: '',
          description: '',
          capacity: '',
          spaceType: 'hall',
          features: '',
          amenities: [],
          availableSetupStyles: [],
        });
      }
      setActiveTab("details"); // Reset to details tab when modal opens
    }
  }, [space, open]);

  const handleSave = async () => {
    if (!formData.name || !formData.capacity) {
      toast({
        title: "Required fields missing",
        description: "Please provide space name and capacity",
        variant: "destructive"
      });
      return;
    }

    setIsSaving(true);
    try {
      const spaceData = {
        name: formData.name,
        description: formData.description,
        capacity: parseInt(formData.capacity),
        spaceType: formData.spaceType,
        features: formData.features,
        amenities: formData.amenities,
        availableSetupStyles: formData.availableSetupStyles,
        ...(isCreating && { venueId })
      };

      const response = isCreating 
        ? await apiRequest("POST", "/api/spaces", spaceData)
        : await apiRequest("PATCH", `/api/spaces/${space.id}`, spaceData);

      // Invalidate multiple relevant queries to ensure UI updates
      await queryClient.invalidateQueries({ queryKey: ["/api/venues"] });
      await queryClient.invalidateQueries({ queryKey: ["/api/venues-with-spaces"] });
      await queryClient.invalidateQueries({ queryKey: ["/api/spaces"] });
      if (venueId) {
        await queryClient.invalidateQueries({ queryKey: [`/api/venues/${venueId}/spaces`] });
      }

      toast({
        title: isCreating ? "Space created" : "Space updated",
        description: `${formData.name} has been ${isCreating ? 'created' : 'updated'} successfully`
      });

      onOpenChange(false);
    } catch (error) {
      toast({
        title: isCreating ? "Creation failed" : "Update failed",
        description: `Could not ${isCreating ? 'create' : 'update'} space`,
        variant: "destructive"
      });
    } finally {
      setIsSaving(false);
    }
  };

  const handleSetupStyleToggle = (styleValue: string) => {
    setFormData(prev => ({
      ...prev,
      availableSetupStyles: prev.availableSetupStyles.includes(styleValue)
        ? prev.availableSetupStyles.filter(s => s !== styleValue)
        : [...prev.availableSetupStyles, styleValue]
    }));
  };

  const handleAmenityToggle = (amenity: string) => {
    setFormData(prev => ({
      ...prev,
      amenities: prev.amenities.includes(amenity)
        ? prev.amenities.filter(a => a !== amenity)
        : [...prev.amenities, amenity]
    }));
  };



  const commonAmenities = [
    'WiFi', 'Projector', 'Sound System', 'Microphone', 'Whiteboard', 'Piano',
    'Bar', 'Kitchen Access', 'Parking', 'Wheelchair Accessible', 'Air Conditioning',
    'Natural Light', 'Stage', 'Dance Floor', 'Outdoor Space'
  ];

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="max-w-6xl max-h-[90vh] overflow-hidden">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <Settings className="w-5 h-5" />
            {isCreating ? `Add Space to ${venueName}` : `Edit Space: ${space?.name}`}
          </DialogTitle>
        </DialogHeader>

        <div className="flex-1">
          <div className="border-b border-gray-200">
            <nav className="-mb-px flex space-x-8">
              <button
                onClick={() => setActiveTab("details")}
                className={`whitespace-nowrap py-2 px-1 border-b-2 font-medium text-sm ${
                  activeTab === "details"
                    ? "border-blue-500 text-blue-600"
                    : "border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300"
                }`}
              >
                Details & Amenities
              </button>
              <button
                onClick={() => setActiveTab("setup")}
                className={`whitespace-nowrap py-2 px-1 border-b-2 font-medium text-sm ${
                  activeTab === "setup"
                    ? "border-blue-500 text-blue-600"
                    : "border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300"
                }`}
              >
                Setup Styles
              </button>

            </nav>
          </div>

          {activeTab === "details" && (
            <div className="space-y-6 max-h-[60vh] overflow-y-auto pt-6">
              <div className="grid grid-cols-2 gap-6">
                <div className="space-y-4">
                  <div>
                    <Label htmlFor="name">Space Name *</Label>
                    <Input
                      id="name"
                      value={formData.name}
                      onChange={(e) => setFormData(prev => ({ ...prev, name: e.target.value }))}
                      placeholder="Enter space name"
                    />
                  </div>

                  <div>
                    <Label htmlFor="description">Description</Label>
                    <Textarea
                      id="description"
                      value={formData.description}
                      onChange={(e) => setFormData(prev => ({ ...prev, description: e.target.value }))}
                      placeholder="Describe this space..."
                      rows={3}
                    />
                  </div>

                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <Label htmlFor="capacity">Capacity *</Label>
                      <Input
                        id="capacity"
                        type="number"
                        value={formData.capacity}
                        onChange={(e) => setFormData(prev => ({ ...prev, capacity: e.target.value }))}
                        placeholder="Max guests"
                      />
                    </div>
                    <div>
                      <Label htmlFor="spaceType">Space Type</Label>
                      <select
                        id="spaceType"
                        value={formData.spaceType}
                        onChange={(e) => setFormData(prev => ({ ...prev, spaceType: e.target.value }))}
                        className="flex h-10 w-full rounded-md border border-input bg-background px-3 py-2 text-sm ring-offset-background file:border-0 file:bg-transparent file:text-sm file:font-medium placeholder:text-muted-foreground focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 disabled:cursor-not-allowed disabled:opacity-50"
                      >
                        <option value="hall">Hall</option>
                        <option value="ballroom">Ballroom</option>
                        <option value="conference">Conference Room</option>
                        <option value="garden">Garden/Outdoor</option>
                        <option value="pavilion">Pavilion</option>
                        <option value="terrace">Terrace</option>
                        <option value="other">Other</option>
                      </select>
                    </div>
                  </div>

                  <div>
                    <Label htmlFor="features">Features & Amenities</Label>
                    <Input
                      id="features"
                      value={formData.features}
                      onChange={(e) => setFormData(prev => ({ ...prev, features: e.target.value }))}
                      placeholder="Stage, Dance floor, Projector, etc."
                    />
                  </div>
                </div>

                <div className="space-y-4">
                  <div>
                    <Label>Amenities</Label>
                    <div className="mt-2 grid grid-cols-2 gap-2 max-h-64 overflow-y-auto">
                      {commonAmenities.map(amenity => (
                        <label key={amenity} className="flex items-center space-x-2 cursor-pointer">
                          <Checkbox
                            checked={formData.amenities.includes(amenity)}
                            onCheckedChange={() => handleAmenityToggle(amenity)}
                          />
                          <span className="text-sm">{amenity}</span>
                        </label>
                      ))}
                    </div>
                  </div>

                  <div>
                    <Label>Selected Amenities</Label>
                    <div className="mt-2 flex flex-wrap gap-2">
                      {formData.amenities.map(amenity => (
                        <Badge key={amenity} variant="secondary" className="cursor-pointer" onClick={() => handleAmenityToggle(amenity)}>
                          {amenity}
                          <X className="w-3 h-3 ml-1" />
                        </Badge>
                      ))}
                      {formData.amenities.length === 0 && (
                        <span className="text-sm text-slate-500">No amenities selected</span>
                      )}
                    </div>
                  </div>
                </div>
              </div>
            </div>
          )}

          {activeTab === "setup" && (
            <div className="space-y-6 max-h-[60vh] overflow-y-auto pt-6">
              <div>
                <h3 className="text-lg font-semibold mb-4">Available Setup Styles</h3>
                <p className="text-sm text-slate-600 mb-6">
                  Select which setup styles are available for this space. This will be shown when creating events.
                </p>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  {SETUP_STYLES.map(style => (
                    <label key={style.value} className="flex items-start space-x-3 cursor-pointer p-4 border rounded-lg hover:bg-slate-50">
                      <Checkbox
                        checked={formData.availableSetupStyles.includes(style.value)}
                        onCheckedChange={() => handleSetupStyleToggle(style.value)}
                        className="mt-1"
                      />
                      <div className="flex-1">
                        <div className="font-medium">{style.label}</div>
                        <div className="text-sm text-slate-600 mt-1">{style.description}</div>
                      </div>
                    </label>
                  ))}
                </div>

                <Separator className="my-6" />

                <div>
                  <h4 className="font-medium mb-3">Selected Setup Styles</h4>
                  <div className="flex flex-wrap gap-2">
                    {formData.availableSetupStyles.map(styleValue => {
                      const style = SETUP_STYLES.find(s => s.value === styleValue);
                      return style ? (
                        <Badge key={styleValue} variant="outline" className="cursor-pointer" onClick={() => handleSetupStyleToggle(styleValue)}>
                          <Grid3X3 className="w-3 h-3 mr-1" />
                          {style.label}
                          <X className="w-3 h-3 ml-1" />
                        </Badge>
                      ) : null;
                    })}
                    {formData.availableSetupStyles.length === 0 && (
                      <span className="text-sm text-slate-500">No setup styles selected</span>
                    )}
                  </div>
                </div>
              </div>
            </div>
          )}


        </div>

        <div className="flex justify-end gap-3 pt-4 border-t">
          <Button variant="outline" onClick={() => onOpenChange(false)}>
            Cancel
          </Button>
          <Button onClick={handleSave} disabled={isSaving}>
            <Save className="w-4 h-4 mr-2" />
            {isSaving 
              ? (isCreating ? "Creating..." : "Saving...") 
              : (isCreating ? "Create Space" : "Save Changes")
            }
          </Button>
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/venues/floor-plan-.content:
import { useState, useRef, useCallback, useEffect } from "react";
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Label } from "@/components/ui/label";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import { RotateCcw, Square, Circle, Save, Plus, Trash2, Move, RotateCw } from "lucide-react";
import { cn } from "@/lib/utils";

interface FloorPlanElement {
  id: string;
  type: 'table' | 'stage' | 'bar' | 'door' | 'wall' | 'chair';
  shape: 'rectangle' | 'circle' | 'square';
  x: number;
  y: number;
  width: number;
  height: number;
  rotation: number;
  seats?: number; // For tables
  label?: string;
  color: string;
}

interface FloorPlanDesignerProps {
  spaceId: string;
  initialFloorPlan?: {
    elements: FloorPlanElement[];
    dimensions: { width: number; height: number };
  };
  onSave: (floorPlan: any) => void;
}

const ELEMENT_TYPES = [
  { type: 'table', label: 'Round Table', shape: 'circle', defaultSize: { width: 60, height: 60 }, color: '#8B4513', icon: Circle },
  { type: 'table', label: 'Rectangle Table', shape: 'rectangle', defaultSize: { width: 80, height: 40 }, color: '#8B4513', icon: Square },
  { type: 'stage', label: 'Stage', shape: 'rectangle', defaultSize: { width: 120, height: 80 }, color: '#4A5568', icon: Square },
  { type: 'bar', label: 'Bar', shape: 'rectangle', defaultSize: { width: 100, height: 30 }, color: '#2D3748', icon: Square },
  { type: 'door', label: 'Door', shape: 'rectangle', defaultSize: { width: 40, height: 10 }, color: '#F7FAFC', icon: Square },
  { type: 'wall', label: 'Wall', shape: 'rectangle', defaultSize: { width: 10, height: 100 }, color: '#1A202C', icon: Square },
];

export function FloorPlanDesigner({ spaceId, initialFloorPlan, onSave }: FloorPlanDesignerProps) {
  const canvasRef = useRef<HTMLDivElement>(null);
  const [elements, setElements] = useState<FloorPlanElement[]>(initialFloorPlan?.elements || []);
  const [selectedElement, setSelectedElement] = useState<string | null>(null);
  const [mode, setMode] = useState<'select' | 'add'>('select');
  const [selectedType, setSelectedType] = useState(ELEMENT_TYPES[0]);
  const [isDragging, setIsDragging] = useState(false);
  const [dragStart, setDragStart] = useState({ x: 0, y: 0 });
  const [canvasSize] = useState({ width: 800, height: 600 });
  const [zoom, setZoom] = useState(100);

  const addElement = useCallback((x: number, y: number) => {
    if (mode !== 'add') return;

    const newElement: FloorPlanElement = {
      id: `element-${Date.now()}`,
      type: selectedType.type as any,
      shape: selectedType.shape as any,
      x: x - selectedType.defaultSize.width / 2,
      y: y - selectedType.defaultSize.height / 2,
      width: selectedType.defaultSize.width,
      height: selectedType.defaultSize.height,
      rotation: 0,
      seats: selectedType.type === 'table' ? (selectedType.shape === 'circle' ? 6 : 8) : undefined,
      label: `${selectedType.label} ${elements.length + 1}`,
      color: selectedType.color,
    };

    setElements(prev => [...prev, newElement]);
    setSelectedElement(newElement.id);
    setMode('select');
  }, [mode, selectedType, elements.length]);

  const updateElement = useCallback((id: string, updates: Partial<FloorPlanElement>) => {
    setElements(prev => prev.map(el => el.id === id ? { ...el, ...updates } : el));
  }, []);

  const deleteElement = useCallback((id: string) => {
    setElements(prev => prev.filter(el => el.id !== id));
    setSelectedElement(null);
  }, []);

  const handleCanvasClick = useCallback((e: React.MouseEvent) => {
    if (!canvasRef.current) return;

    const rect = canvasRef.current.getBoundingClientRect();
    const x = (e.clientX - rect.left) * (canvasSize.width / rect.width);
    const y = (e.clientY - rect.top) * (canvasSize.height / rect.height);

    if (mode === 'add') {
      addElement(x, y);
    } else {
      setSelectedElement(null);
    }
  }, [mode, addElement, canvasSize]);

  const handleElementClick = useCallback((e: React.MouseEvent, elementId: string) => {
    e.stopPropagation();
    setSelectedElement(elementId);
  }, []);

  const handleElementMouseDown = useCallback((e: React.MouseEvent, elementId: string) => {
    if (mode !== 'select') return;

    e.stopPropagation();
    setIsDragging(true);
    setSelectedElement(elementId);
    setDragStart({ x: e.clientX, y: e.clientY });
  }, [mode]);

  const handleMouseMove = useCallback((e: React.MouseEvent) => {
    if (!isDragging || !selectedElement || !canvasRef.current) return;

    const rect = canvasRef.current.getBoundingClientRect();
    const scale = canvasSize.width / rect.width;
    const deltaX = (e.clientX - dragStart.x) * scale;
    const deltaY = (e.clientY - dragStart.y) * scale;

    updateElement(selectedElement, {
      x: Math.max(0, Math.min(canvasSize.width - 100, elements.find(el => el.id === selectedElement)!.x + deltaX)),
      y: Math.max(0, Math.min(canvasSize.height - 100, elements.find(el => el.id === selectedElement)!.y + deltaY))
    });

    setDragStart({ x: e.clientX, y: e.clientY });
  }, [isDragging, selectedElement, dragStart, updateElement, elements, canvasSize]);

  const handleMouseUp = useCallback(() => {
    setIsDragging(false);
  }, []);

  const selectedElementData = elements.find(el => el.id === selectedElement);

  const handleSave = () => {
    const floorPlan = {
      elements,
      dimensions: canvasSize,
      lastUpdated: new Date().toISOString()
    };
    onSave(floorPlan);
  };

  return (
    <div className="space-y-6">
      {/* Toolbar */}
      <Card className="p-4">
        <div className="flex items-center justify-between">
          <div className="flex items-center gap-4">
            <div className="flex items-center gap-2">
              <Button
                variant={mode === 'select' ? 'default' : 'outline'}
                size="sm"
                onClick={() => setMode('select')}
              >
                <Move className="w-4 h-4 mr-1" />
                Select
              </Button>
              <Button
                variant={mode === 'add' ? 'default' : 'outline'}
                size="sm"
                onClick={() => setMode('add')}
              >
                <Plus className="w-4 h-4 mr-1" />
                Add
              </Button>
            </div>

            {mode === 'add' && (
              <div className="flex items-center gap-2">
                <Label className="text-sm">Element:</Label>
                <Select
                  value={selectedType.label}
                  onValueChange={(value) => {
                    const type = ELEMENT_TYPES.find(t => t.label === value)!;
                    setSelectedType(type);
                  }}
                >
                  <SelectTrigger className="w-40">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    {ELEMENT_TYPES.map(type => (
                      <SelectItem key={type.label} value={type.label}>
                        {type.label}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>
            )}

            <Separator orientation="vertical" className="h-6" />

            <div className="flex items-center gap-2">
              <Label className="text-sm">Zoom:</Label>
              <input
                type="range"
                value={zoom}
                onChange={(e) => setZoom(parseInt(e.target.value))}
                min={50}
                max={200}
                step={10}
                className="w-20"
              />
              <span className="text-sm text-slate-600 w-12">{zoom}%</span>
            </div>
          </div>

          <div className="flex items-center gap-2">
            <Button variant="outline" size="sm" onClick={() => setElements([])}>
              <RotateCcw className="w-4 h-4 mr-1" />
              Clear
            </Button>
            <Button size="sm" onClick={handleSave}>
              <Save className="w-4 h-4 mr-1" />
              Save Layout
            </Button>
          </div>
        </div>
      </Card>

      <div className="flex gap-6">
        {/* Canvas */}
        <Card className="flex-1 p-4">
          <div className="relative overflow-hidden rounded-lg border bg-slate-50" style={{ height: '500px' }}>
            <div
              ref={canvasRef}
              className="relative bg-white border-2 border-dashed border-slate-300 cursor-crosshair mx-auto mt-8"
              style={{
                width: `${canvasSize.width * (zoom / 100)}px`,
                height: `${canvasSize.height * (zoom / 100)}px`,
                transform: `scale(${zoom / 100})`,
                transformOrigin: 'top left'
              }}
              onClick={handleCanvasClick}
              onMouseMove={handleMouseMove}
              onMouseUp={handleMouseUp}
            >
              {/* Grid */}
              <svg className="absolute inset-0 pointer-events-none opacity-20">
                <defs>
                  <pattern id="grid" width="20" height="20" patternUnits="userSpaceOnUse">
                    <path d="M 20 0 L 0 0 0 20" fill="none" stroke="#E2E8F0" strokeWidth="1"/>
                  </pattern>
                </defs>
                <rect width="100%" height="100%" fill="url(#grid)" />
              </svg>

              {/* Elements */}
              {elements.map(element => (
                <div
                  key={element.id}
                  className={cn(
                    "absolute border-2 cursor-move flex items-center justify-center text-xs font-medium text-white select-none",
                    selectedElement === element.id ? "border-blue-500 shadow-lg" : "border-slate-400"
                  )}
                  style={{
                    left: element.x,
                    top: element.y,
                    width: element.width,
                    height: element.height,
                    backgroundColor: element.color,
                    borderRadius: element.shape === 'circle' ? '50%' : '4px',
                    transform: `rotate(${element.rotation}deg)`,
                  }}
                  onClick={(e) => handleElementClick(e, element.id)}
                  onMouseDown={(e) => handleElementMouseDown(e, element.id)}
                >
                  <span className="text-center leading-tight">
                    {element.type === 'table' && element.seats && (
                      <div>{element.seats} seats</div>
                    )}
                    {element.label && element.type !== 'table' && (
                      <div>{element.label}</div>
                    )}
                  </span>
                </div>
              ))}

              {/* Selection Handles */}
              {selectedElementData && (
                <>
                  <div
                    className="absolute w-2 h-2 bg-blue-500 border border-white rounded-full cursor-nw-resize"
                    style={{
                      left: selectedElementData.x - 4,
                      top: selectedElementData.y - 4,
                    }}
                  />
                  <div
                    className="absolute w-2 h-2 bg-blue-500 border border-white rounded-full cursor-ne-resize"
                    style={{
                      left: selectedElementData.x + selectedElementData.width - 4,
                      top: selectedElementData.y - 4,
                    }}
                  />
                  <div
                    className="absolute w-2 h-2 bg-blue-500 border border-white rounded-full cursor-sw-resize"
                    style={{
                      left: selectedElementData.x - 4,
                      top: selectedElementData.y + selectedElementData.height - 4,
                    }}
                  />
                  <div
                    className="absolute w-2 h-2 bg-blue-500 border border-white rounded-full cursor-se-resize"
                    style={{
                      left: selectedElementData.x + selectedElementData.width - 4,
                      top: selectedElementData.y + selectedElementData.height - 4,
                    }}
                  />
                </>
              )}
            </div>

            {/* Instructions */}
            {elements.length === 0 && (
              <div className="absolute inset-0 flex items-center justify-center pointer-events-none">
                <div className="text-center text-slate-500">
                  <p className="text-lg font-medium">Design Your Floor Plan</p>
                  <p className="text-sm mt-2">Click "Add" and select elements to place them on the canvas</p>
                  <p className="text-sm">Drag elements to move them around</p>
                </div>
              </div>
            )}
          </div>
        </Card>

        {/* Properties Panel */}
        {selectedElementData && (
          <Card className="w-72 p-4">
            <div className="space-y-4">
              <div className="flex items-center justify-between">
                <h3 className="font-medium">Element Properties</h3>
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => deleteElement(selectedElementData.id)}
                >
                  <Trash2 className="w-4 h-4" />
                </Button>
              </div>

              <div className="space-y-3">
                <div>
                  <Label className="text-sm">Label</Label>
                  <Input
                    value={selectedElementData.label || ''}
                    onChange={(e) => updateElement(selectedElementData.id, { label: e.target.value })}
                    className="mt-1"
                    placeholder="Element label"
                  />
                </div>

                <div className="grid grid-cols-2 gap-2">
                  <div>
                    <Label className="text-sm">Width</Label>
                    <Input
                      type="number"
                      value={selectedElementData.width}
                      onChange={(e) => updateElement(selectedElementData.id, { width: parseInt(e.target.value) || 50 })}
                      className="mt-1"
                      min="10"
                      max="200"
                    />
                  </div>
                  <div>
                    <Label className="text-sm">Height</Label>
                    <Input
                      type="number"
                      value={selectedElementData.height}
                      onChange={(e) => updateElement(selectedElementData.id, { height: parseInt(e.target.value) || 50 })}
                      className="mt-1"
                      min="10"
                      max="200"
                    />
                  </div>
                </div>

                {selectedElementData.type === 'table' && (
                  <div>
                    <Label className="text-sm">Number of Seats</Label>
                    <Input
                      type="number"
                      value={selectedElementData.seats || 6}
                      onChange={(e) => updateElement(selectedElementData.id, { seats: parseInt(e.target.value) || 6 })}
                      className="mt-1"
                      min="2"
                      max="12"
                    />
                  </div>
                )}

                <div>
                  <Label className="text-sm">Rotation</Label>
                  <div className="flex items-center gap-2 mt-1">
                    <input
                      type="range"
                      value={selectedElementData.rotation}
                      onChange={(e) => updateElement(selectedElementData.id, { rotation: parseInt(e.target.value) })}
                      min={0}
                      max={360}
                      step={15}
                      className="flex-1"
                    />
                    <span className="text-sm text-slate-600 w-8">{selectedElementData.rotation}Â°</span>
                  </div>
                </div>

                <div>
                  <Label className="text-sm">Color</Label>
                  <div className="flex gap-2 mt-1">
                    {['#8B4513', '#4A5568', '#2D3748', '#1A202C', '#E53E3E', '#3182CE', '#38A169', '#D69E2E'].map(color => (
                      <button
                        key={color}
                        className={cn(
                          "w-6 h-6 rounded border-2",
                          selectedElementData.color === color ? "border-slate-900" : "border-slate-300"
                        )}
                        style={{ backgroundColor: color }}
                        onClick={() => updateElement(selectedElementData.id, { color })}
                      />
                    ))}
                  </div>
                </div>
              </div>
            </div>
          </Card>
        )}
      </div>

      {/* Stats */}
      <Card className="p-4">
        <div className="flex items-center justify-between">
          <div className="flex items-center gap-6">
            <div className="text-sm">
              <span className="font-medium">Elements:</span> {elements.length}
            </div>
            <div className="text-sm">
              <span className="font-medium">Tables:</span> {elements.filter(el => el.type === 'table').length}
            </div>
            <div className="text-sm">
              <span className="font-medium">Total Seats:</span> {elements.filter(el => el.type === 'table').reduce((sum, el) => sum + (el.seats || 0), 0)}
            </div>
          </div>
          <Badge variant="outline">
            Canvas: {canvasSize.width} Ã— {canvasSize.height}
          </Badge>
        </div>
      </Card>
    </div>
  );
}

