  y: number;
  width: number;
  height: number;
  rotation: number;
  seats?: number;
  label?: string;
  color: string;
  opacity?: number;
  zIndex?: number;
  locked?: boolean;
  visible?: boolean;
  borderWidth?: number;
  borderColor?: string;
  gradient?: boolean;
