        variant: "destructive"
      });
      return;
    }

    testNotificationMutation.mutate({ type: testType, customerId: selectedCustomer });
  };

  const handleSendPaymentReminders = () => {
    sendPaymentRemindersMutation.mutate();
  };

  if (statsLoading) {
    return (
