// Tenant Actions Component
function TenantActions({ tenant }: { tenant: any }) {
  const [showEditDialog, setShowEditDialog] = useState(false);
  const [showViewDialog, setShowViewDialog] = useState(false);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const deleteTenantMutation = useMutation({
    mutationFn: async (id: string) => {
      return apiRequest("DELETE", `/api/super-admin/tenants/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/super-admin/tenants"] });
      toast({
        title: "Success",
        description: "Account deleted successfully",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to delete account",
        variant: "destructive",
      });
    },
  });

  return (
    <>
      <div className="flex items-center gap-2">
        <Button 
          variant="ghost" 
          size="sm"
          onClick={() => setShowViewDialog(true)}
        >
          <Eye className="w-4 h-4" />
        </Button>
        <Button 
          variant="ghost" 
          size="sm"
          onClick={() => setShowEditDialog(true)}
        >
          <Edit className="w-4 h-4" />
        </Button>
        <Button 
          variant="ghost" 
          size="sm" 
          className="text-red-600"
          onClick={() => {
            if (confirm(`Are you sure you want to delete the account "${tenant.name}"? This action cannot be undone.`)) {
              deleteTenantMutation.mutate(tenant.id);
            }
          }}
          disabled={deleteTenantMutation.isPending}
        >
          <Trash2 className="w-4 h-4" />
        </Button>
      </div>

      {/* Edit Tenant Dialog */}
      <EditTenantDialog 
        isOpen={showEditDialog}
        onClose={() => setShowEditDialog(false)}
        tenant={tenant}
      />

      {/* View Tenant Dialog */}
      <ViewTenantDialog 
        isOpen={showViewDialog}
        onClose={() => setShowViewDialog(false)}
        tenant={tenant}
      />
    </>
  );
}

