import { useEffect, useState } from "react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Building2, Users, Calendar, DollarSign, Settings, LogOut } from "lucide-react";
import { useToast } from "@/hooks/use-toast";

interface TenantSession {
  tenantId: string;
  userId: string;
  userRole: string;
  tenantName: string;
  packageFeatures: any;
}

interface TenantUser {
  id: string;
  name: string;
  email: string;
  role: string;
}

interface Tenant {
  id: string;
  name: string;
  status: string;
  packageId: string;
}

export default function TenantDashboard() {
  const { toast } = useToast();
  const [session, setSession] = useState<TenantSession | null>(null);
  const [user, setUser] = useState<TenantUser | null>(null);
  const [tenant, setTenant] = useState<Tenant | null>(null);

  useEffect(() => {
    // Check if user is authenticated
    const sessionData = localStorage.getItem("tenantSession");
    const userData = localStorage.getItem("tenantUser");
    const tenantData = localStorage.getItem("tenant");

    if (!sessionData || !userData || !tenantData) {
      // Redirect to login if no session
      window.location.href = "/tenant/login";
      return;
    }

    try {
      setSession(JSON.parse(sessionData));
      setUser(JSON.parse(userData));
      setTenant(JSON.parse(tenantData));
    } catch (error) {
      console.error("Error parsing session data:", error);
      handleLogout();
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("tenantSession");
    localStorage.removeItem("tenantUser");
    localStorage.removeItem("tenant");

    toast({
      title: "Logged Out",
      description: "You have been successfully logged out.",
    });

    window.location.href = "/tenant/login";
  };

  if (!session || !user || !tenant) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full" />
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
      {/* Header */}
      <header className="bg-white dark:bg-gray-800 shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            <div className="flex items-center">
              <Building2 className="h-8 w-8 text-blue-600 mr-3" />
              <div>
                <h1 className="text-lg font-semibold text-gray-900 dark:text-white">
                  {tenant.name}
                </h1>
                <p className="text-sm text-gray-500 dark:text-gray-400">
                  Venue Management Dashboard
                </p>
              </div>
            </div>

            <div className="flex items-center space-x-4">
