    console.log('Table columns:');
    tableInfo.rows.forEach(col => {
      console.log(`  ${col.column_name}: ${col.data_type} (nullable: ${col.is_nullable})`);
    });

    // Test 3: Direct package insertion
    console.log('\n3. Testing direct package creation...');
