      res.status(201).json(tour);
    } catch (error) {
      console.error("Error creating tour:", error);
      res.status(500).json({ message: "Failed to create tour" });
    }
  });

  app.patch("/api/tours/:id", async (req, res) => {
    try {
      const { id } = req.params;
      const updateData = req.body;
      const updatedTour = await storage.updateTour(id, updateData);

      if (!updatedTour) {
        return res.status(404).json({ message: "Tour not found" });
      }

      res.json(updatedTour);
    } catch (error) {
      console.error("Error updating tour:", error);
      res.status(500).json({ message: "Failed to update tour" });
    }
  });

  // Convert Lead to Customer
  app.post("/api/leads/:id/convert", async (req, res) => {
    try {
      const { id } = req.params;
      const lead = await storage.getLead(id);

      if (!lead) {
        return res.status(404).json({ message: "Lead not found" });
      }

      // Create customer from lead data
      const customer = await storage.createCustomer({
        name: `${lead.firstName} ${lead.lastName}`,
        email: lead.email,
        phone: lead.phone || "",
        company: "", // Could be added to lead model if needed
        notes: lead.notes || ""
      });

      // Update lead with converted customer ID and status
      await storage.updateLead(id, {
        convertedCustomerId: customer.id,
        status: "WON"
      });

      // Log conversion activity
      await storage.createLeadActivity({
        leadId: id,
        type: "STATUS_CHANGE",
        body: `Lead converted to customer: ${customer.name}`,
        meta: { 
          customerId: customer.id,
          conversionDate: new Date().toISOString()
        }
      });

      res.json({
        customer,
        lead: await storage.getLead(id)
      });
    } catch (error) {
      console.error("Error converting lead to customer:", error);
      res.status(500).json({ message: "Failed to convert lead to customer" });
    }
  });

  // Notification System Endpoints
  // Test notification settings
  app.post("/api/notifications/test", async (req, res) => {
    try {
      const { type, customerId } = req.body;

      if (!customerId) {
        return res.status(400).json({ message: "Customer ID required" });
      }

      const customer = await storage.getCustomer(customerId);
      if (!customer) {
        return res.status(404).json({ message: "Customer not found" });
      }

      if (!customer.email) {
        return res.status(400).json({ message: "Customer email not found" });
      }

      // Get notification preferences from settings
      const settings = await storage.getSettings();
      const notificationPrefs = {
        emailNotifications: settings.notifications?.emailNotifications ?? true,
        pushNotifications: settings.notifications?.pushNotifications ?? false,
        bookingConfirmations: settings.notifications?.bookingConfirmations ?? true,
        paymentReminders: settings.notifications?.paymentReminders ?? true,
        maintenanceAlerts: settings.notifications?.maintenanceAlerts ?? true
      };

      if (!notificationPrefs.emailNotifications) {
        return res.status(400).json({ 
          message: "Email notifications are disabled in settings",
          settings: notificationPrefs
        });
      }

      const notificationService = new NotificationService(gmailService, notificationPrefs);

      switch (type) {
        case 'booking':
          if (!notificationPrefs.bookingConfirmations) {
            return res.status(400).json({ message: "Booking confirmations are disabled" });
          }

          // Create a test booking for notification
          const testBooking = {
            id: 'test-booking',
            eventName: 'Test Event Booking',
            eventType: 'corporate',
            eventDate: new Date(),
            startTime: '18:00',
            endTime: '23:00',
            guestCount: 50,
            venueId: 'test-venue',
            customerId: customer.id,
            status: 'confirmed',
            totalAmount: '2500.00',
            createdAt: new Date()
          } as any;

          const bookingResult = await notificationService.sendBookingConfirmation(testBooking, customer);
          res.json({ 
            success: bookingResult, 
            message: bookingResult ? 'Test booking confirmation sent' : 'Failed to send booking confirmation',
            type: 'booking',
            customer: { name: customer.name, email: customer.email }
          });
          break;

        case 'payment':
          if (!notificationPrefs.paymentReminders) {
            return res.status(400).json({ message: "Payment reminders are disabled" });
          }

          const testBookingForPayment = {
            id: 'test-payment-booking',
            eventName: 'Test Payment Event',
            eventType: 'wedding',
            eventDate: new Date(Date.now() + 7 * 24 * 60 * 60 * 1000), // 1 week from now
            startTime: '16:00',
            endTime: '22:00',
            guestCount: 100,
            venueId: 'test-venue',
            customerId: customer.id,
            status: 'confirmed',
            totalAmount: '5000.00',
            createdAt: new Date()
          } as any;

          const paymentResult = await notificationService.sendPaymentReminder(testBookingForPayment, customer, 1500);
          res.json({ 
            success: paymentResult, 
            message: paymentResult ? 'Test payment reminder sent' : 'Failed to send payment reminder',
            type: 'payment',
            customer: { name: customer.name, email: customer.email }
          });
          break;

        case 'maintenance':
          if (!notificationPrefs.maintenanceAlerts) {
            return res.status(400).json({ message: "Maintenance alerts are disabled" });
          }

          const maintenanceResult = await notificationService.sendMaintenanceAlert(
            'System maintenance scheduled for this weekend. Please backup your data and expect brief downtime between 2-4 AM on Sunday.',
            [customer.email]
          );
          res.json({ 
            success: maintenanceResult, 
            message: maintenanceResult ? 'Test maintenance alert sent' : 'Failed to send maintenance alert',
            type: 'maintenance',
            customer: { name: customer.name, email: customer.email }
          });
          break;

        default:
          return res.status(400).json({ 
            message: "Invalid notification type. Use: booking, payment, or maintenance" 
          });
      }
    } catch (error: any) {
      console.error('Notification test error:', error);
      res.status(500).json({ 
        message: "Failed to send test notification",
        error: error.message,
        details: error.stack
      });
    }
  });

  // Send payment reminders for overdue bookings
  app.post("/api/notifications/payment-reminders", async (req, res) => {
    try {
      const settings = await storage.getSettings();
      const notificationPrefs = {
        emailNotifications: settings.notifications?.emailNotifications ?? true,
        pushNotifications: settings.notifications?.pushNotifications ?? false,
        bookingConfirmations: settings.notifications?.bookingConfirmations ?? true,
        paymentReminders: settings.notifications?.paymentReminders ?? true,
        maintenanceAlerts: settings.notifications?.maintenanceAlerts ?? true
      };

      if (!notificationPrefs.emailNotifications || !notificationPrefs.paymentReminders) {
        return res.status(400).json({ 
          message: "Payment reminders are disabled in settings",
          settings: notificationPrefs
        });
      }

      const notificationService = new NotificationService(gmailService, notificationPrefs);
      const bookings = await storage.getBookings();
      const customers = await storage.getCustomers();

      // Find bookings with outstanding payments (where deposit is not paid)
      const overdueBookings = bookings.filter(booking => 
        booking.status === 'confirmed' && 
        !booking.depositPaid &&
        booking.customerId &&
        booking.totalAmount
      );

      const results = [];
      for (const booking of overdueBookings) {
        const customer = customers.find(c => c.id === booking.customerId);
        if (customer && customer.email) {
          const amountDue = booking.depositAmount ? parseFloat(booking.depositAmount) : parseFloat(booking.totalAmount!) * 0.3;

          try {
            const success = await notificationService.sendPaymentReminder(booking, customer, amountDue);
            results.push({
              bookingId: booking.id,
              customerEmail: customer.email,
              success,
              amountDue
            });
          } catch (error: any) {
            results.push({
              bookingId: booking.id,
              customerEmail: customer.email,
              success: false,
              error: error.message
            });
          }
        }
      }

      res.json({
        message: `Processed ${results.length} payment reminders`,
        results,
        settings: notificationPrefs
      });
    } catch (error: any) {
      console.error('Payment reminders error:', error);
      res.status(500).json({ message: "Failed to send payment reminders" });
    }
  });

  // Get notification stats
  app.get("/api/notifications/stats", async (req, res) => {
    try {
      const settings = await storage.getSettings();
      const notificationPrefs = {
        emailNotifications: settings.notifications?.emailNotifications ?? true,
        pushNotifications: settings.notifications?.pushNotifications ?? false,
        bookingConfirmations: settings.notifications?.bookingConfirmations ?? true,
        paymentReminders: settings.notifications?.paymentReminders ?? true,
        maintenanceAlerts: settings.notifications?.maintenanceAlerts ?? true
      };

      const bookings = await storage.getBookings();
      const confirmedBookings = bookings.filter(b => b.status === 'confirmed');
      const overduePayments = bookings.filter(b => 
        b.status === 'confirmed' && 
        !b.depositPaid &&
        b.totalAmount
      );

      res.json({
        notificationSettings: notificationPrefs,
        stats: {
          totalBookings: bookings.length,
          confirmedBookings: confirmedBookings.length,
          overduePayments: overduePayments.length,
          gmailConfigured: gmailService ? true : false
        }
      });
    } catch (error: any) {
      console.error('Notification stats error:', error);
      res.status(500).json({ message: "Failed to get notification stats" });
    }
  });

  // ===== PUBLIC API ENDPOINTS =====
  // Public plans endpoint (for pricing page) - uses actual feature packages from super admin
  app.get("/api/public/plans", async (req, res) => {
    try {
      // Get active feature packages from database
      const result = await db.execute(sql`
        SELECT 
          id, name, slug, description, 
          price_monthly, price_yearly, 
          limits, features, billing_modes, 
          trial_days, status
        FROM feature_packages 
        WHERE status = 'active'
        ORDER BY price_monthly ASC NULLS LAST
      `);

      const plans = result.rows.map((pkg: any) => {
        const limits = typeof pkg.limits === 'string' ? JSON.parse(pkg.limits) : pkg.limits;
        const features = typeof pkg.features === 'string' ? JSON.parse(pkg.features) : pkg.features;
        const billingModes = typeof pkg.billing_modes === 'string' ? JSON.parse(pkg.billing_modes) : pkg.billing_modes;

        // Convert feature flags to human readable feature list
        const featureList = [];
        const featureMap = {
          'dashboard-analytics': 'Smart booking management',
          'event-management': 'Event & Booking Management',
          'customer-management': 'Customer database',
          'lead-management': 'Lead management & scoring',
          'proposal-system': 'Professional proposals',
          'stripe-payments': 'Stripe payment processing',
          'venue-management': 'Multi-venue management',
          'service-packages': 'Service & package management',
          'gmail-integration': 'Gmail integration',
          'task-management': 'Task & team management',
          'ai-voice-booking': 'AI voice-to-text booking',
          'ai-scheduling': 'Smart AI scheduling',
          'ai-email-replies': 'AI email auto-replies',
          'ai-lead-scoring': 'AI lead priority scoring',
          'ai-insights': 'AI-powered insights',
          'ai-proposal-generation': 'AI proposal content generation',
          'mobile-responsive': 'Mobile-responsive interface',
          'audit-logs': 'Audit logging & security',
          'custom-branding': 'Custom branding & themes',
          'priority-support': 'Priority customer support',
          'api-access': 'API access',
          'advanced-reporting': 'Advanced reports & export',
          'calendar-integration': 'Calendar integration',
          'floor-plan-designer': '2D floor plan designer'
        };

        Object.entries(features || {}).forEach(([key, enabled]) => {
          if (enabled && featureMap[key]) {
            featureList.push(featureMap[key]);
          }
        });

        // Add basic features for empty lists
        if (featureList.length === 0) {
          featureList.push('Basic venue management', 'Email support', 'Mobile access');
        }

        return {
          id: pkg.id,
          name: pkg.name,
          slug: pkg.slug,
          description: pkg.description || `Perfect for ${pkg.name.toLowerCase()} venue management needs.`,
          billingModes: billingModes || {
            monthly: { amount: Math.round((pkg.price_monthly || 29.99) * 100), currency: "USD" },
            yearly: { amount: Math.round((pkg.price_yearly || 299.90) * 100), currency: "USD" }
          },
          limits: {
            venues: limits?.maxVenues || 1,
            staff: limits?.maxUsers || 5,
            monthlyBookings: limits?.maxBookings || 100,
            maxSpacesPerVenue: limits?.maxSpacesPerVenue || 10
          },
          flags: {
            stripe: features?.['stripe-payments'] || false,
            analytics: features?.['dashboard-analytics'] || false,
            customBranding: features?.['custom-branding'] || false,
            prioritySupport: features?.['priority-support'] || false
          },
          trialDays: pkg.trial_days || 14,
          features: featureList
        };
      });

      res.json(plans);
    } catch (error) {
      console.error("Error fetching plans:", error);
      res.status(500).json({ message: "Failed to fetch plans" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

