import { useState } from "react";
import { useLocation } from "wouter";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { toast } from "@/hooks/use-toast";
import { Shield, ArrowRight } from "lucide-react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";

const loginSchema = z.object({
  email: z.string().email("Please enter a valid email address"),
  password: z.string().min(1, "Password is required"),
});

type LoginData = z.infer<typeof loginSchema>;

export default function SuperAdminDirectAccess() {
  const [, setLocation] = useLocation();
  const [isSubmitting, setIsSubmitting] = useState(false);

  const form = useForm<LoginData>({
    resolver: zodResolver(loginSchema),
    defaultValues: {
      email: "yonasfasil.sl@gmail.com", // Pre-filled for convenience
      password: "",
    },
  });

  const onSubmit = async (data: LoginData) => {
    setIsSubmitting(true);

    try {
      const response = await fetch('/api/auth/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(data),
        credentials: 'include',
      });

      const result = await response.json();

      if (response.ok && result.user?.isSuperAdmin) {
        toast({
          title: "Super Admin Access Granted",
          description: "Welcome to the platform administration panel",
        });
        setLocation('/admin');
      } else if (response.ok && !result.user?.isSuperAdmin) {
        toast({
          title: "Access Denied",
          description: "This account does not have super admin privileges",
          variant: "destructive",
        });
      } else {
        toast({
          title: "Login Failed",
          description: result.message || "Invalid credentials",
          variant: "destructive",
        });
      }
    } catch (error) {
      console.error('Super admin login error:', error);
      toast({
        title: "Error",
        description: "Login failed. Please try again.",
        variant: "destructive",
      });
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-red-50 to-orange-50 p-4">
      <Card className="w-full max-w-md border-red-200">
        <CardHeader className="text-center">
          <div className="flex items-center justify-center mb-4">
            <Shield className="h-8 w-8 text-red-600" />
          </div>
          <CardTitle className="text-2xl font-bold text-red-700">Super Admin Access</CardTitle>
          <CardDescription>
            Platform Administration Panel
          </CardDescription>
        </CardHeader>
        <CardContent>
          <Form {...form}>
            <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
              <FormField
                control={form.control}
                name="email"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Super Admin Email</FormLabel>
                    <FormControl>
                      <Input
                        {...field}
                        type="email"
                        disabled={isSubmitting}
                        className="bg-red-50/50"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="password"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Password</FormLabel>
                    <FormControl>
                      <Input
                        {...field}
                        type="password"
                        disabled={isSubmitting}
                        className="bg-red-50/50"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <Button 
                type="submit" 
                className="w-full bg-red-600 hover:bg-red-700"
                disabled={isSubmitting}
              >
                {isSubmitting ? (
                  "Authenticating..."
                ) : (
                  <>
                    Access Admin Panel
                    <ArrowRight className="ml-2 h-4 w-4" />
                  </>
                )}
              </Button>
            </form>
          </Form>

          <div className="mt-6 p-4 bg-yellow-50 border border-yellow-200 rounded-lg">
            <div className="flex items-start">
              <Shield className="h-4 w-4 text-yellow-600 mt-0.5 mr-2" />
              <div className="text-sm text-yellow-700">
                <p className="font-medium">Super Admin Panel Includes:</p>
                <ul className="mt-2 list-disc list-inside space-y-1">
                  <li>User Management & Analytics</li>
                  <li>Tenant Oversight & Control</li>
                  <li>Feature Package Management</li>
                  <li>Platform Analytics & Reports</li>
                  <li>User Impersonation for Support</li>
                </ul>
              </div>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/feature-gating/Fea.content:
import React from 'react';
import { Button } from '@/components/ui/button';
import { useTenantFeatures } from '@/hooks/useTenantFeatures';
import { Lock } from 'lucide-react';
import { toast } from '@/hooks/use-toast';

interface FeatureButtonProps extends React.ComponentProps<typeof Button> {
  feature: string;
  children: React.ReactNode;
  upgradeMessage?: string;
}

export function FeatureButton({ 
  feature, 
  children, 
  onClick,
  upgradeMessage,
  ...props 
}: FeatureButtonProps) {
  const { hasFeature, tenantInfo } = useTenantFeatures();

  const handleClick = (e: React.MouseEvent<HTMLButtonElement>) => {
    if (!hasFeature(feature)) {
      e.preventDefault();
      toast({
        title: "Upgrade Required",
        description: upgradeMessage || `This feature requires a higher plan. Upgrade your ${tenantInfo?.planName || 'current'} plan to continue.`,
        variant: "destructive",
      });
      return;
    }

    if (onClick) {
      onClick(e);
    }
  };

  return (
    <Button 
      {...props}
      onClick={handleClick}
      disabled={!hasFeature(feature) || props.disabled}
      className={`${props.className} ${!hasFeature(feature) ? 'opacity-75' : ''}`}
    >
      {!hasFeature(feature) && <Lock className="w-4 h-4 mr-2" />}
      {children}
    </Button>
  );
}

import React from 'react';
import { useTenantFeatures } from '@/hooks/useTenantFeatures';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Lock, Crown, Zap } from 'lucide-react';

interface FeatureGateProps {
  feature: string;
  children: React.ReactNode;
  fallback?: React.ReactNode;
  showUpgradePrompt?: boolean;
  upgradeMessage?: string;
}

export function FeatureGate({ 
  feature, 
  children, 
  fallback, 
  showUpgradePrompt = true,
  upgradeMessage
}: FeatureGateProps) {
  const { hasFeature, tenantInfo, isLoading } = useTenantFeatures();

  if (isLoading) {
    return (
      <div className="h-8 bg-gray-200 animate-pulse rounded"></div>
    );
  }

  if (hasFeature(feature)) {
    return <>{children}</>;
  }

  if (fallback) {
    return <>{fallback}</>;
  }

  if (!showUpgradePrompt) {
    return null;
  }

  return (
    <FeatureUpgradePrompt 
      feature={feature} 
      message={upgradeMessage}
      currentPlan={tenantInfo?.planName || 'Free'}
    />
  );
}

interface FeatureUpgradePromptProps {
  feature: string;
  message?: string;
  currentPlan: string;
}

function FeatureUpgradePrompt({ feature, message, currentPlan }: FeatureUpgradePromptProps) {
  const featureNames: Record<string, string> = {
    'venue-management': 'Venue Management',
    'customer-management': 'Customer Management', 
    'lead-management': 'Lead Management',
    'proposal-system': 'Proposal System',
    'task-management': 'Task Management',
    'ai-features': 'AI Features',
    'stripe-payments': 'Payment Processing',
    'gmail-integration': 'Gmail Integration',
    'reporting-analytics': 'Advanced Reporting',
    'custom-branding': 'Custom Branding',
    'api-access': 'API Access',
    'priority-support': 'Priority Support'
  };

  const featureName = featureNames[feature] || feature;

  const handleUpgrade = () => {
    // Navigate to billing/upgrade page
    window.location.href = '/billing';
  };

  return (
    <Card className="border-amber-200 bg-amber-50">
      <CardHeader className="pb-3">
        <CardTitle className="flex items-center gap-2 text-amber-800">
          <Lock className="w-5 h-5" />
          {featureName} Upgrade Required
        </CardTitle>
      </CardHeader>
      <CardContent className="pt-0">
        <p className="text-sm text-amber-700 mb-4">
          {message || `${featureName} is not available in your ${currentPlan} plan. Upgrade to access this feature.`}
        </p>
        <div className="flex gap-2">
          <Button 
            onClick={handleUpgrade}
            className="bg-amber-600 hover:bg-amber-700 text-white"
            size="sm"
          >
            <Crown className="w-4 h-4 mr-2" />
            Upgrade Plan
          </Button>
          <Button variant="outline" size="sm">
            Learn More
          </Button>
        </div>
      </CardContent>
    </Card>
  );
}

// Usage limit display component
interface UsageLimitDisplayProps {
  limitName: string;
  currentUsage: number;
  maxAllowed: number;
  unitName?: string;
}

export function UsageLimitDisplay({ 
  limitName, 
  currentUsage, 
  maxAllowed,
  unitName = 'items'
}: UsageLimitDisplayProps) {
  const usagePercentage = maxAllowed > 0 ? (currentUsage / maxAllowed) * 100 : 0;
  const isNearLimit = usagePercentage >= 80;
  const isAtLimit = currentUsage >= maxAllowed;

  return (
    <Card className={`${isAtLimit ? 'border-red-200 bg-red-50' : isNearLimit ? 'border-amber-200 bg-amber-50' : ''}`}>
      <CardContent className="p-4">
        <div className="flex items-center justify-between mb-2">
          <span className="text-sm font-medium">
            {limitName} Usage
          </span>
          <span className={`text-sm font-medium ${isAtLimit ? 'text-red-600' : isNearLimit ? 'text-amber-600' : 'text-green-600'}`}>
            {currentUsage} / {maxAllowed === -1 ? 'âˆž' : maxAllowed} {unitName}
          </span>
        </div>

        {maxAllowed !== -1 && (
          <div className="w-full bg-gray-200 rounded-full h-2 mb-2">
            <div 
              className={`h-2 rounded-full transition-all ${
                isAtLimit ? 'bg-red-500' : isNearLimit ? 'bg-amber-500' : 'bg-green-500'
              }`}
              style={{ width: `${Math.min(usagePercentage, 100)}%` }}
            />
          </div>
        )}

        {isAtLimit && (
          <div className="flex items-center gap-2 mt-2">
            <Zap className="w-4 h-4 text-red-500" />
            <p className="text-xs text-red-600">
              You've reached your limit. Upgrade to continue.
            </p>
          </div>
        )}

        {isNearLimit && !isAtLimit && (
          <div className="flex items-center gap-2 mt-2">
            <Zap className="w-4 h-4 text-amber-500" />
            <p className="text-xs text-amber-600">
              You're approaching your limit. Consider upgrading.
            </p>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

client/src/pages/auth/SignupWithPackages.content:
import { useState, useEffect } from "react";
import { Link, useLocation } from "wouter";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Badge } from "@/components/ui/badge";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";
import { Label } from "@/components/ui/label";
import { Calendar, AlertCircle, Eye, EyeOff, Check, Zap, Users, Building, Crown } from "lucide-react";
import { toast } from "@/hooks/use-toast";
import { useQuery } from "@tanstack/react-query";

const signupSchema = z.object({
  email: z.string().email("Please enter a valid email address"),
  password: z.string().min(6, "Password must be at least 6 characters"),
  confirmPassword: z.string().min(6, "Password must be at least 6 characters"),
  firstName: z.string().min(2, "First name must be at least 2 characters"),
  lastName: z.string().min(2, "Last name must be at least 2 characters"),
  packageId: z.string().min(1, "Please select a package"),
  companyName: z.string().min(2, "Company name must be at least 2 characters"),
}).refine((data) => data.password === data.confirmPassword, {
  message: "Passwords don't match",
  path: ["confirmPassword"],
});

type SignupData = z.infer<typeof signupSchema>;

interface FeaturePackage {
  id: string;
  name: string;
  description: string;
  price: number;
  billingCycle: string;
  features: Record<string, boolean>;
  limits: Record<string, number>;
}

export default function SignupWithPackages() {
  const [, setLocation] = useLocation();
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [error, setError] = useState<string>("");
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [currentStep, setCurrentStep] = useState(1);

  const { data: packages = [], isLoading: packagesLoading } = useQuery({
    queryKey: ['/api/public/plans'],
  });

  const form = useForm<SignupData>({
    resolver: zodResolver(signupSchema),
    defaultValues: {
      email: "",
      password: "",
      confirmPassword: "",
      firstName: "",
      lastName: "",
      packageId: "",
      companyName: "",
    },
  });

  const selectedPackage = (packages as FeaturePackage[]).find((pkg: FeaturePackage) => pkg.id === form.watch('packageId'));

  const onSubmit = async (data: SignupData) => {
    setIsSubmitting(true);
    setError("");

    try {
      const response = await fetch('/api/auth/signup', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(data),
        credentials: 'include',
      });

      if (response.ok) {
        const result = await response.json();
        toast({
          title: "Account created!",
          description: "Welcome to VENUIN. Redirecting to your dashboard...",
        });
        // Redirect to tenant dashboard
        if (result.tenant?.slug) {
          setLocation(`/t/${result.tenant.slug}/app`);
        } else {
          setLocation('/');
        }
      } else {
        const result = await response.json();
        setError(result.message || "Failed to create account");
      }
    } catch (error) {
      console.error('Signup error:', error);
      setError("Failed to create account. Please try again.");
    } finally {
      setIsSubmitting(false);
    }
  };

  const getPackageIcon = (packageId: string) => {
    switch (packageId) {
      case 'starter': return <Building className="w-6 h-6" />;
      case 'professional': return <Zap className="w-6 h-6" />;
      case 'enterprise': return <Crown className="w-6 h-6" />;
      default: return <Building className="w-6 h-6" />;
    }
  };

  const getPackageColor = (packageId: string) => {
    switch (packageId) {
      case 'starter': return 'border-blue-200 bg-blue-50 hover:border-blue-300';
      case 'professional': return 'border-purple-200 bg-purple-50 hover:border-purple-300';
      case 'enterprise': return 'border-orange-200 bg-orange-50 hover:border-orange-300';
      default: return 'border-gray-200 bg-gray-50 hover:border-gray-300';
    }
  };

  const renderPackageSelection = () => (
    <div className="space-y-6">
      <div className="text-center">
        <h2 className="text-2xl font-bold">Choose Your Plan</h2>
        <p className="text-gray-600 mt-2">Select the perfect plan for your venue business</p>
      </div>

      {packagesLoading ? (
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          {[1, 2, 3].map((i) => (
            <div key={i} className="h-64 bg-gray-100 animate-pulse rounded-lg"></div>
          ))}
        </div>
      ) : (
        <FormField
          control={form.control}
          name="packageId"
          render={({ field }) => (
            <FormItem>
              <FormControl>
                <RadioGroup
                  onValueChange={field.onChange}
                  value={field.value}
                  className="grid grid-cols-1 md:grid-cols-3 gap-4"
                >
                  {(packages as FeaturePackage[]).map((pkg: FeaturePackage) => (
                    <div key={pkg.id} className="relative">
                      <RadioGroupItem
                        value={pkg.id}
                        id={pkg.id}
                        className="sr-only"
                      />
                      <Label
                        htmlFor={pkg.id}
                        className={`block cursor-pointer rounded-lg border-2 p-6 transition-all ${
                          field.value === pkg.id 
                            ? 'border-blue-500 bg-blue-50 ring-2 ring-blue-200' 
                            : getPackageColor(pkg.id)
                        }`}
                      >
                        <div className="text-center">
                          <div className="flex justify-center mb-4">
                            {getPackageIcon(pkg.id)}
                          </div>
                          <h3 className="text-xl font-semibold">{pkg.name}</h3>
                          <div className="mt-2">
                            <span className="text-3xl font-bold">${pkg.price}</span>
                            <span className="text-gray-500">/{pkg.billingCycle}</span>
                          </div>
                          <p className="text-sm text-gray-600 mt-2">{pkg.description}</p>

                          <div className="mt-4 space-y-2">
                            {Object.entries(pkg.features || {})
                              .filter(([_, enabled]) => enabled)
                              .slice(0, 5)
                              .map(([feature, _]) => (
                                <div key={feature} className="flex items-center text-sm">
                                  <Check className="w-4 h-4 text-green-500 mr-2" />
                                  <span className="capitalize">
                                    {feature.replace(/_/g, ' ')}
                                  </span>
                                </div>
                              ))}
                          </div>

                          {pkg.limits && (
                            <div className="mt-4 pt-4 border-t border-gray-200">
                              <div className="text-sm text-gray-600">
                                {pkg.limits.max_bookings_per_month === -1 ? (
                                  <span>Unlimited bookings</span>
                                ) : (
                                  <span>{pkg.limits.max_bookings_per_month} bookings/month</span>
                                )}
                              </div>
                            </div>
                          )}
                        </div>

                        {field.value === pkg.id && (
                          <div className="absolute top-2 right-2">
                            <div className="w-6 h-6 bg-blue-500 rounded-full flex items-center justify-center">
                              <Check className="w-4 h-4 text-white" />
                            </div>
                          </div>
                        )}
                      </Label>
                    </div>
                  ))}
                </RadioGroup>
              </FormControl>
              <FormMessage />
            </FormItem>
          )}
        />
      )}

      <div className="flex justify-between">
        <div></div>
        <Button 
          type="button" 
          onClick={() => setCurrentStep(2)}
          disabled={!form.watch('packageId')}
        >
          Continue to Account Details
        </Button>
      </div>
    </div>
  );

  const renderAccountForm = () => (
    <div className="space-y-6">
      <div className="text-center">
        <h2 className="text-2xl font-bold">Create Your Account</h2>
        <p className="text-gray-600 mt-2">
          Set up your account for the <strong>{selectedPackage?.name}</strong> plan
        </p>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        <FormField
          control={form.control}
          name="firstName"
          render={({ field }) => (
            <FormItem>
              <FormLabel>First Name</FormLabel>
              <FormControl>
                <Input
                  {...field}
                  placeholder="Enter your first name"
                  disabled={isSubmitting}
                />
              </FormControl>
              <FormMessage />
            </FormItem>
          )}
        />

        <FormField
          control={form.control}
          name="lastName"
          render={({ field }) => (
            <FormItem>
              <FormLabel>Last Name</FormLabel>
              <FormControl>
                <Input
                  {...field}
                  placeholder="Enter your last name"
                  disabled={isSubmitting}
                />
              </FormControl>
              <FormMessage />
            </FormItem>
          )}
        />
      </div>

      <FormField
        control={form.control}
        name="email"
        render={({ field }) => (
          <FormItem>
            <FormLabel>Email</FormLabel>
            <FormControl>
              <Input
                {...field}
                type="email"
                placeholder="Enter your email"
                disabled={isSubmitting}
              />
            </FormControl>
            <FormMessage />
          </FormItem>
        )}
      />

      <FormField
        control={form.control}
        name="companyName"
        render={({ field }) => (
          <FormItem>
            <FormLabel>Company Name</FormLabel>
            <FormControl>
              <Input
                {...field}
                placeholder="Enter your company/venue name"
                disabled={isSubmitting}
              />
            </FormControl>
            <FormMessage />
          </FormItem>
        )}
      />

      <FormField
        control={form.control}
        name="password"
        render={({ field }) => (
          <FormItem>
            <FormLabel>Password</FormLabel>
            <FormControl>
              <div className="relative">
                <Input
                  {...field}
                  type={showPassword ? "text" : "password"}
                  placeholder="Create a password (min 6 characters)"
                  disabled={isSubmitting}
                />
                <Button
                  type="button"
                  variant="ghost"
                  size="sm"
                  className="absolute right-0 top-0 h-full px-3 py-2 hover:bg-transparent"
                  onClick={() => setShowPassword(!showPassword)}
                  disabled={isSubmitting}
                >
                  {showPassword ? (
                    <EyeOff className="h-4 w-4" />
                  ) : (
                    <Eye className="h-4 w-4" />
                  )}
                </Button>
              </div>
            </FormControl>
            <FormMessage />
          </FormItem>
        )}
      />

      <FormField
        control={form.control}
        name="confirmPassword"
        render={({ field }) => (
          <FormItem>
            <FormLabel>Confirm Password</FormLabel>
            <FormControl>
              <div className="relative">
                <Input
                  {...field}
                  type={showConfirmPassword ? "text" : "password"}
                  placeholder="Confirm your password"
                  disabled={isSubmitting}
                />
                <Button
                  type="button"
                  variant="ghost"
                  size="sm"
                  className="absolute right-0 top-0 h-full px-3 py-2 hover:bg-transparent"
                  onClick={() => setShowConfirmPassword(!showConfirmPassword)}
                  disabled={isSubmitting}
                >
                  {showConfirmPassword ? (
                    <EyeOff className="h-4 w-4" />
                  ) : (
                    <Eye className="h-4 w-4" />
                  )}
                </Button>
              </div>
            </FormControl>
            <FormMessage />
          </FormItem>
        )}
      />

      <div className="flex justify-between">
        <Button 
          type="button" 
          variant="outline"
          onClick={() => setCurrentStep(1)}
        >
          Back to Plans
        </Button>
        <Button 
          type="submit" 
          disabled={isSubmitting}
        >
          {isSubmitting ? "Creating account..." : "Create Account"}
        </Button>
      </div>
    </div>
  );

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex items-center justify-center p-4">
      <Card className="w-full max-w-6xl">
        <CardHeader className="text-center">
          <CardTitle className="flex items-center justify-center gap-2">
            <Calendar className="w-6 h-6" />
            Join VENUIN
          </CardTitle>
          <CardDescription>
            Create your account to start managing your venue
          </CardDescription>
        </CardHeader>
        <CardContent>
          {error && (
            <Alert variant="destructive" className="mb-4">
              <AlertCircle className="h-4 w-4" />
              <AlertDescription>{error}</AlertDescription>
            </Alert>
          )}

          <Form {...form}>
            <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
              {currentStep === 1 && renderPackageSelection()}
              {currentStep === 2 && renderAccountForm()}
            </form>
          </Form>

          <div className="mt-6 text-center">
            <p className="text-sm text-gray-600">
              Already have an account?{" "}
              <Link href="/login" className="text-blue-600 hover:underline">
                Sign in
              </Link>
            </p>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

shared/status-utils.ts.content:
// Event status management utilities

export type EventStatus = 
  | "inquiry" 
  | "pending" 
  | "tentative" 
  | "confirmed" 
  | "confirmed_deposit_paid" 
  | "confirmed_fully_paid" 
  | "completed" 
  | "cancelled";

export interface StatusConfig {
  label: string;
  description: string;
  color: string;
  bgColor: string;
  borderColor: string;
  textColor: string;
  nextStatuses: EventStatus[];
  canEdit: boolean;
  isCompleted: boolean;
  isActive: boolean;
}

export const STATUS_CONFIGS: Record<EventStatus, StatusConfig> = {
  inquiry: {
    label: "Inquiry",
    description: "Initial inquiry received, needs follow-up",
    color: "#6b7280", // gray-500
    bgColor: "bg-gray-50",
    borderColor: "border-gray-200", 
    textColor: "text-gray-700",
    nextStatuses: ["pending", "cancelled"],
    canEdit: true,
    isCompleted: false,
    isActive: true
  },
  pending: {
    label: "Proposal Shared", 
    description: "Proposal sent to client, awaiting response",
    color: "#f59e0b", // amber-500
    bgColor: "bg-amber-50",
    borderColor: "border-amber-200",
    textColor: "text-amber-700",
    nextStatuses: ["tentative", "cancelled"],
    canEdit: true,
    isCompleted: false,
    isActive: true
  },
  tentative: {
    label: "Tentative",
    description: "Tentative booking, awaiting confirmation",
    color: "#3b82f6", // blue-500
    bgColor: "bg-blue-50",
    borderColor: "border-blue-200",
    textColor: "text-blue-700",
    nextStatuses: ["confirmed_deposit_paid", "cancelled"],
    canEdit: true,
    isCompleted: false,
    isActive: true
  },
  confirmed: {
    label: "Confirmed",
    description: "Booking confirmed by client",
    color: "#10b981", // emerald-500
    bgColor: "bg-emerald-50",
    borderColor: "border-emerald-200",
    textColor: "text-emerald-700",
    nextStatuses: ["completed", "cancelled"],
    canEdit: true,
    isCompleted: false,
    isActive: true
  },
  confirmed_deposit_paid: {
    label: "Confirmed Deposited",
    description: "Deposit received, balance payment pending",
    color: "#8b5cf6", // violet-500  
    bgColor: "bg-violet-50",
    borderColor: "border-violet-200",
    textColor: "text-violet-700",
    nextStatuses: ["confirmed_fully_paid", "cancelled"],
    canEdit: true,
    isCompleted: false,
    isActive: true
  },
  confirmed_fully_paid: {
    label: "Confirmed Paid",
    description: "All payments received, event ready",
    color: "#10b981", // emerald-500
    bgColor: "bg-emerald-50", 
    borderColor: "border-emerald-200",
    textColor: "text-emerald-700",
    nextStatuses: ["completed", "cancelled"],
    canEdit: true,
    isCompleted: false,
    isActive: true
  },
  completed: {
    label: "Completed",
    description: "Event successfully completed",
    color: "#059669", // emerald-600
    bgColor: "bg-green-50",
    borderColor: "border-green-200",
    textColor: "text-green-700",
    nextStatuses: [],
    canEdit: false,
    isCompleted: true,
    isActive: false
  },
  cancelled: {
    label: "Cancelled",
    description: "Event has been cancelled",
    color: "#dc2626", // red-600
    bgColor: "bg-red-50",
    borderColor: "border-red-200", 
    textColor: "text-red-700",
    nextStatuses: [],
    canEdit: false,
    isCompleted: true,
    isActive: false
  }
};

// Get status configuration with legacy mapping
export function getStatusConfig(status: EventStatus | string): StatusConfig {
  // Handle legacy statuses by mapping them to new ones
  const statusMap: Record<string, EventStatus> = {
    'confirmed': 'confirmed',
    'confirmed_fully_paid': 'confirmed_fully_paid',
    'confirmed_deposit_paid': 'confirmed_deposit_paid',
    'pending': 'pending',
    'proposal_shared': 'pending',
    'completed': 'completed',
    'cancelled': 'cancelled',
    'inquiry': 'inquiry',
    'tentative': 'tentative'
  };

  const mappedStatus = statusMap[status] || status as EventStatus;
  return STATUS_CONFIGS[mappedStatus] || STATUS_CONFIGS['inquiry'];
}

// Get status color for calendar display
export function getStatusColor(status: EventStatus | string): string {
  // Handle legacy statuses by mapping them to new ones
  const statusMap: Record<string, EventStatus> = {
    'confirmed': 'confirmed',
    'confirmed_fully_paid': 'confirmed_fully_paid',
    'confirmed_deposit_paid': 'confirmed_deposit_paid',
    'pending': 'pending',
    'proposal_shared': 'pending',
    'completed': 'completed',
    'cancelled': 'cancelled',
    'inquiry': 'inquiry',
    'tentative': 'tentative'
  };

  const mappedStatus = statusMap[status] || status as EventStatus;
  return STATUS_CONFIGS[mappedStatus]?.color || STATUS_CONFIGS['inquiry'].color;
}

// Get status label
export function getStatusLabel(status: EventStatus | string): string {
  // Handle legacy statuses by mapping them to new ones
  const statusMap: Record<string, EventStatus> = {
    'confirmed': 'confirmed',
    'confirmed_fully_paid': 'confirmed_fully_paid',
    'confirmed_deposit_paid': 'confirmed_deposit_paid',
    'pending': 'pending',
    'proposal_shared': 'pending',
    'completed': 'completed',
    'cancelled': 'cancelled',
    'inquiry': 'inquiry',
    'tentative': 'tentative'
  };

  const mappedStatus = statusMap[status] || status as EventStatus;
  return STATUS_CONFIGS[mappedStatus]?.label || status.toString();
}

// Get next possible statuses
export function getNextStatuses(currentStatus: EventStatus | string): EventStatus[] {
  // Handle legacy statuses by mapping them to new ones
  const statusMap: Record<string, EventStatus> = {
    'confirmed': 'confirmed',
    'confirmed_fully_paid': 'confirmed_fully_paid',
    'confirmed_deposit_paid': 'confirmed_deposit_paid',
    'pending': 'pending',
    'proposal_shared': 'pending',
    'completed': 'completed',
    'cancelled': 'cancelled',
    'inquiry': 'inquiry',
    'tentative': 'tentative'
  };

  const mappedStatus = statusMap[currentStatus] || currentStatus as EventStatus;
  return STATUS_CONFIGS[mappedStatus]?.nextStatuses || [];
}

// Check if status can be edited
export function canEditStatus(status: EventStatus | string): boolean {
  // Handle legacy statuses by mapping them to new ones
  const statusMap: Record<string, EventStatus> = {
    'confirmed': 'tentative',
    'confirmed_fully_paid': 'confirmed',
    'confirmed_deposit_paid': 'confirmed',
    'pending': 'pending',
    'proposal_shared': 'pending',
    'completed': 'completed',
    'cancelled': 'cancelled'
  };

  const mappedStatus = statusMap[status] || status as EventStatus;
  return STATUS_CONFIGS[mappedStatus]?.canEdit ?? true; // Default to true if status not found
}

// Check if event is completed
export function isEventCompleted(status: EventStatus): boolean {
  return STATUS_CONFIGS[status].isCompleted;
}

// Check if event is active (can have future actions)
export function isEventActive(status: EventStatus): boolean {
  return STATUS_CONFIGS[status].isActive;
}

// Auto-update status based on payment state
export function getAutoStatus(
  proposalStatus: string | null,
  depositPaid: boolean, 
  totalPaid: number,
  totalAmount: number,
  eventDate: Date,
  currentStatus: EventStatus
): EventStatus {
  const now = new Date();
  const eventPassed = eventDate < now;

  // If event is completed, don't change
  if (currentStatus === "completed" || currentStatus === "cancelled") {
    return currentStatus;
  }

  // Auto-complete if event passed and fully paid
  if (eventPassed && totalPaid >= totalAmount) {
    return "completed";
  }

  // If fully paid
  if (totalPaid >= totalAmount) {
    return "confirmed_fully_paid";
  }

  // If deposit paid
  if (depositPaid) {
    return "confirmed_deposit_paid";
  }

  // If proposal accepted but no payment
  if (proposalStatus === "accepted") {
    return "tentative";
  }

  // If proposal sent
  if (proposalStatus === "sent" || proposalStatus === "viewed") {
    return "pending";
  }

  // Default to inquiry
  return "inquiry";
}

// Get status badge classes for UI components
export function getStatusBadgeClasses(status: EventStatus): string {
  const config = STATUS_CONFIGS[status];
  return `${config.bgColor} ${config.textColor} ${config.borderColor} border`;
}

// Get all statuses for dropdowns/selects
export function getAllStatuses(): { value: EventStatus; label: string; description: string }[] {
  return Object.entries(STATUS_CONFIGS).map(([value, config]) => ({
    value: value as EventStatus,
    label: config.label,
    description: config.description
  }));
}

client/src/components/events/status-sele.content:
import { useState } from "react";
import { Badge } from "@/components/ui/badge";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { 
  getStatusConfig, 
  getNextStatuses, 
  canEditStatus,
  getAllStatuses, 
  type EventStatus 
} from "@shared/status-utils";
import { CancellationModal } from "./cancellation-modal";

interface StatusSelectorProps {
  currentStatus: EventStatus | string;
  onStatusChange?: (newStatus: EventStatus) => void;
  readonly?: boolean;
  showAllStatuses?: boolean;
  eventId?: string;
  eventTitle?: string;
}

export function StatusSelector({ 
  currentStatus, 
  onStatusChange, 
  readonly = false,
  showAllStatuses = false,
  eventId,
  eventTitle 
}: StatusSelectorProps) {
  const [showCancellationModal, setShowCancellationModal] = useState(false);
  const statusConfig = getStatusConfig(currentStatus);

  // If readonly, just show the current status badge
  if (readonly || !canEditStatus(currentStatus as EventStatus)) {
    return (
      <Badge 
        className={`${statusConfig.bgColor} ${statusConfig.textColor} ${statusConfig.borderColor} border text-sm px-3 py-1`}
      >
        {statusConfig.label}
      </Badge>
    );
  }

  // Determine available statuses - show all statuses for easy selection in modal
  const availableStatuses = getAllStatuses();

  const handleStatusChange = (value: string) => {
    if (value === "cancelled") {
      // Show cancellation modal instead of directly changing status
      setShowCancellationModal(true);
    } else {
      onStatusChange?.(value as EventStatus);
    }
  };

  return (
    <>
      <Select
        value={currentStatus}
        onValueChange={handleStatusChange}
      >
        <SelectTrigger className="w-fit min-w-32 h-9">
          <SelectValue>
            <Badge 
              className={`${statusConfig.bgColor} ${statusConfig.textColor} ${statusConfig.borderColor} border text-sm px-3 py-1`}
            >
              {statusConfig.label}
            </Badge>
          </SelectValue>
        </SelectTrigger>
        <SelectContent>
          {availableStatuses.map((status) => {
            const config = getStatusConfig(status.value);
            return (
              <SelectItem key={status.value} value={status.value}>
                <div className="flex items-center gap-2">
                  <div 
                    className="w-2 h-2 rounded-full"
                    style={{ backgroundColor: config.color }}
                  />
                  <div className="flex flex-col">
                    <span className="font-medium">{config.label}</span>
                    <span className="text-xs text-slate-500">{config.description}</span>
                  </div>
                </div>
              </SelectItem>
            );
          })}
        </SelectContent>
      </Select>

      {eventId && eventTitle && (
        <CancellationModal
          isOpen={showCancellationModal}
          onClose={() => setShowCancellationModal(false)}
          eventId={eventId}
          eventTitle={eventTitle}
        />
      )}
    </>
  );
}

interface StatusBadgeProps {
  status: EventStatus | string;
  size?: "sm" | "md" | "lg";
}

export function StatusBadge({ status, size = "md" }: StatusBadgeProps) {
  const config = getStatusConfig(status);

  const sizeClasses = {
    sm: "text-xs px-2 py-1",
    md: "text-sm px-3 py-1",
    lg: "text-base px-4 py-2"
  };

  return (
    <Badge 
      className={`${config.bgColor} ${config.textColor} ${config.borderColor} border ${sizeClasses[size]}`}
    >
      {config.label}
    </Badge>
  );
}

interface StatusIndicatorProps {
  status: EventStatus | string;
  size?: number;
}

export function StatusIndicator({ status, size = 8 }: StatusIndicatorProps) {
  const config = getStatusConfig(status);

  return (
    <div 
      className="rounded-full"
      style={{ 
        backgroundColor: config.color,
        width: `${size}px`,
        height: `${size}px`
      }}
      title={config.label}
    />
  );
}

