import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { X } from "lucide-react";

interface CancellationModalProps {
  isOpen: boolean;
  onClose: () => void;
  eventId: string;
  eventTitle: string;
}

const CANCELLATION_REASONS = [
  "Client requested cancellation",
  "Venue unavailable",
  "Weather conditions", 
  "Budget constraints",
  "Schedule conflict",
  "Health/safety concerns",
  "Vendor issues",
  "Other"
];

export function CancellationModal({ isOpen, onClose, eventId, eventTitle }: CancellationModalProps) {
  const [selectedReason, setSelectedReason] = useState("");
  const [customReason, setCustomReason] = useState("");
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const cancelEventMutation = useMutation({
    mutationFn: async ({ reason }: { reason: string }) => {
      return await apiRequest("PATCH", `/api/bookings/${eventId}`, {
        status: "cancelled",
        cancellationReason: reason,
        cancelledAt: new Date().toISOString()
      });
    },
    onSuccess: () => {
      toast({
        title: "Event Cancelled",
        description: `"${eventTitle}" has been cancelled successfully.`,
      });

      // Invalidate relevant queries
      queryClient.invalidateQueries({ queryKey: ["/api/bookings"] });
      queryClient.invalidateQueries({ queryKey: ["/api/calendar/events"] });
      queryClient.invalidateQueries({ queryKey: ["/api/dashboard/metrics"] });

      onClose();
      resetForm();
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: "Failed to cancel event. Please try again.",
        variant: "destructive",
      });
      console.error("Failed to cancel event:", error);
    },
  });

  const resetForm = () => {
    setSelectedReason("");
    setCustomReason("");
  };

  const handleSubmit = () => {
    const finalReason = selectedReason === "Other" ? customReason : selectedReason;

    if (!finalReason.trim()) {
      toast({
        title: "Reason Required",
        description: "Please select or enter a cancellation reason.",
        variant: "destructive",
      });
      return;
    }

    cancelEventMutation.mutate({ reason: finalReason.trim() });
  };

  const handleClose = () => {
    if (cancelEventMutation.isPending) return;
    onClose();
    resetForm();
  };

  return (
    <Dialog open={isOpen} onOpenChange={handleClose}>
      <DialogContent className="sm:max-w-md">
        <DialogHeader className="flex flex-row items-center justify-between">
          <DialogTitle>Cancel Event</DialogTitle>
          <Button
            variant="ghost"
            size="icon"
            onClick={handleClose}
            disabled={cancelEventMutation.isPending}
          >
            <X className="h-4 w-4" />
          </Button>
        </DialogHeader>

        <div className="space-y-6">
          <div className="text-sm text-gray-600">
            Are you sure you want to cancel "{eventTitle}"? Please provide a reason for cancellation.
          </div>

          <div className="space-y-4">
            <Label className="text-base font-medium">Reason for Cancellation</Label>

            <RadioGroup 
              value={selectedReason} 
              onValueChange={setSelectedReason}
              className="space-y-2"
            >
              {CANCELLATION_REASONS.map((reason) => (
                <div key={reason} className="flex items-center space-x-2">
                  <RadioGroupItem value={reason} id={reason} />
                  <Label 
                    htmlFor={reason}
                    className="text-sm font-normal cursor-pointer flex-1"
                  >
                    {reason}
                  </Label>
                </div>
              ))}
            </RadioGroup>

            {selectedReason === "Other" && (
              <div className="space-y-2">
                <Label htmlFor="custom-reason" className="text-sm">
                  Please specify:
                </Label>
                <Textarea
                  id="custom-reason"
                  placeholder="Enter the specific reason for cancellation..."
                  value={customReason}
                  onChange={(e) => setCustomReason(e.target.value)}
                  className="min-h-[80px]"
                />
              </div>
            )}
          </div>

          <div className="flex gap-3 justify-end">
            <Button
              variant="outline"
              onClick={handleClose}
              disabled={cancelEventMutation.isPending}
            >
              Keep Event
            </Button>
            <Button
              onClick={handleSubmit}
              disabled={cancelEventMutation.isPending || !selectedReason}
              variant="destructive"
            >
              {cancelEventMutation.isPending ? "Cancelling..." : "Cancel Event"}
            </Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

server/middleware/security.ts.content:
import helmet from 'helmet';
import rateLimit from 'express-rate-limit';
import mongoSanitize from 'express-mongo-sanitize';
import compression from 'compression';
import cors from 'cors';
import type { Express, Request, Response, NextFunction } from 'express';

// Security middleware configuration
export function setupSecurity(app: Express) {
  // Helmet for security headers
  app.use(helmet({
    contentSecurityPolicy: {
      directives: {
        defaultSrc: ["'self'"],
        styleSrc: ["'self'", "'unsafe-inline'", "https://fonts.googleapis.com"],
        fontSrc: ["'self'", "https://fonts.gstatic.com"],
        imgSrc: ["'self'", "data:", "https:", "blob:"],
        scriptSrc: ["'self'", "'unsafe-inline'", "'unsafe-eval'", "https://js.stripe.com"], // Added Stripe.js
        connectSrc: ["'self'", "https://api.stripe.com", "ws:", "wss:"],
        frameSrc: ["'self'", "https://js.stripe.com"],
      },
    },
    crossOriginEmbedderPolicy: false, // Disable for better compatibility
  }));

  // CORS configuration
  const corsOptions = {
    origin: function (origin: string | undefined, callback: (err: Error | null, allow?: boolean) => void) {
      // In development, allow all origins for easier testing
      if (process.env.NODE_ENV === 'development') {
        return callback(null, true);
      }

      const allowedOrigins = (process.env.CORS_ORIGINS || 'http://localhost:5000').split(',');

      // Allow requests with no origin (mobile apps, Postman, etc.)
      if (!origin) return callback(null, true);

      if (allowedOrigins.indexOf(origin) !== -1) {
        callback(null, true);
      } else {
        callback(new Error('Not allowed by CORS'));
      }
    },
    credentials: true,
    methods: ['GET', 'POST', 'PUT', 'DELETE', 'PATCH', 'OPTIONS'],
    allowedHeaders: ['Content-Type', 'Authorization', 'X-Requested-With'],
  };

  app.use(cors(corsOptions));

  // Rate limiting
  const rateLimitWindowMs = parseInt(process.env.RATE_LIMIT_WINDOW_MS || '900000'); // 15 minutes
  const rateLimitMax = parseInt(process.env.RATE_LIMIT_MAX_REQUESTS || '100');

  // General rate limit (more permissive in development)
  const generalLimiter = rateLimit({
    windowMs: rateLimitWindowMs,
    max: process.env.NODE_ENV === 'development' ? 10000 : rateLimitMax, // Much higher limit in dev
    message: {
      error: 'Too many requests from this IP, please try again later.',
      retryAfter: Math.ceil(rateLimitWindowMs / 1000),
    },
    standardHeaders: true,
    legacyHeaders: false,
    // Fix for trust proxy issue in development
    keyGenerator: process.env.NODE_ENV === 'development' 
      ? (req) => 'dev-key' // Single key for all requests in dev
      : undefined, // Use default IP-based key in production
    skip: (req) => {
      // Skip rate limiting for static assets and vite HMR
      return req.path.includes('.') || req.path.startsWith('/src/') || req.path.startsWith('/@vite') || req.path.startsWith('/node_modules');
    }
  });

  // Strict rate limit for auth endpoints
  const authLimiter = rateLimit({
    windowMs: 15 * 60 * 1000, // 15 minutes
    max: process.env.NODE_ENV === 'development' ? 1000 : 5, // Higher limit in dev
    message: {
      error: 'Too many authentication attempts, please try again later.',
      retryAfter: 900, // 15 minutes
    },
    standardHeaders: true,
    legacyHeaders: false,
    // Fix for trust proxy issue in development
    keyGenerator: process.env.NODE_ENV === 'development' 
      ? (req) => 'dev-auth-key' // Single key for all requests in dev
      : undefined, // Use default IP-based key in production
  });

  // API rate limit for high-frequency endpoints
  const apiLimiter = rateLimit({
    windowMs: 15 * 60 * 1000, // 15 minutes
    max: 1000, // Higher limit for API endpoints
    message: {
      error: 'API rate limit exceeded, please try again later.',
      retryAfter: 900,
    },
    standardHeaders: true,
    legacyHeaders: false,
  });

  // Apply rate limiting
  app.use('/api/auth', authLimiter);
  app.use('/api/super-admin/login', authLimiter);
  app.use('/api/public/signup', authLimiter);
  app.use('/api', apiLimiter);
  app.use(generalLimiter);

  // Data sanitization against NoSQL query injection
  app.use(mongoSanitize());

  // Compression middleware
  app.use(compression());

  // Custom security headers
  app.use((req: Request, res: Response, next: NextFunction) => {
    // Remove X-Powered-By header
    res.removeHeader('X-Powered-By');

    // Add custom security headers
    res.setHeader('X-Content-Type-Options', 'nosniff');
    res.setHeader('X-Frame-Options', 'DENY');
    res.setHeader('X-XSS-Protection', '1; mode=block');
    res.setHeader('Referrer-Policy', 'strict-origin-when-cross-origin');

    // HSTS for HTTPS (only in production)
    if (process.env.NODE_ENV === 'production') {
      res.setHeader('Strict-Transport-Security', 'max-age=31536000; includeSubDomains; preload');
    }

    next();
  });
}

// Input validation middleware
export function validateInput(req: Request, res: Response, next: NextFunction) {
  // Basic input sanitization
  const sanitizeObject = (obj: any): any => {
    if (typeof obj === 'string') {
      // Remove potential XSS patterns
      return obj.replace(/<script\b[^<]*(?:(?!<\/script>)<[^<]*)*<\/script>/gi, '')
               .replace(/javascript:/gi, '')
               .replace(/vbscript:/gi, '')
               .replace(/onload/gi, '')
               .replace(/onerror/gi, '');
    }

    if (Array.isArray(obj)) {
      return obj.map(sanitizeObject);
    }

    if (obj && typeof obj === 'object') {
      const sanitized: any = {};
      for (const key in obj) {
        if (obj.hasOwnProperty(key)) {
          sanitized[key] = sanitizeObject(obj[key]);
        }
      }
      return sanitized;
    }

    return obj;
  };

  if (req.body) {
    req.body = sanitizeObject(req.body);
  }

  if (req.query) {
    req.query = sanitizeObject(req.query);
  }

  if (req.params) {
    req.params = sanitizeObject(req.params);
  }

  next();
}

// Request size limiting
export function setupRequestLimits(app: Express) {
  const maxRequestSize = process.env.MAX_REQUEST_SIZE || '10mb';

  app.use((req: Request, res: Response, next: NextFunction) => {
    // Set timeout for requests
    req.setTimeout(30000, () => {
      res.status(408).json({ error: 'Request timeout' });
    });

    next();
  });
}

// Error handling middleware
export function setupErrorHandling(app: Express) {
  // 404 handler
  app.use((req: Request, res: Response, next: NextFunction) => {
    if (req.path.startsWith('/api/')) {
      res.status(404).json({ 
        error: 'API endpoint not found',
        path: req.path,
        method: req.method 
      });
    } else {
      next();
    }
  });

  // Global error handler
  app.use((err: any, req: Request, res: Response, next: NextFunction) => {
    console.error('Global error handler:', err);

    // Don't leak error details in production
    const isDevelopment = process.env.NODE_ENV === 'development';

    const status = err.status || err.statusCode || 500;
    const message = isDevelopment ? err.message : 'Internal server error';
    const stack = isDevelopment ? err.stack : undefined;

    res.status(status).json({
      error: message,
      ...(isDevelopment && { stack, details: err })
    });
  });
}

// Logging middleware
export function setupLogging(app: Express) {
  if (process.env.NODE_ENV === 'production') {
    app.use((req: Request, res: Response, next: NextFunction) => {
      const start = Date.now();

      res.on('finish', () => {
        const duration = Date.now() - start;
        const logData = {
          method: req.method,
          url: req.url,
          status: res.statusCode,
          duration: `${duration}ms`,
          ip: req.ip || req.connection.remoteAddress,
          userAgent: req.headers['user-agent'],
          timestamp: new Date().toISOString(),
        };

        console.log(JSON.stringify(logData));
      });

      next();
    });
  }
}

server/middleware/tenant-isolation.ts.content:
import { Request, Response, NextFunction } from 'express';
import { db } from '../db';
import { users, tenants } from '@shared/schema';
import { eq } from 'drizzle-orm';

// Extend Express Request to include tenant info
declare global {
  namespace Express {
    interface Request {
      user?: {
        id: string;
        tenantId: string | null;
        role: string;
        permissions: string[];
      };
      tenant?: {
        id: string;
        name: string;
        status: string;
        subscriptionPackageId: string;
      };
    }
  }
}

/**
 * Extract tenant information from subdomain or custom domain
 */
export function extractTenantFromHost(hostname: string): string | null {
  // Handle localhost development
  if (hostname === 'localhost' || hostname.startsWith('localhost:')) {
    return null; // Super admin or development mode
  }

  // Extract subdomain (e.g., "marriott" from "marriott.yourdomain.com")
  const parts = hostname.split('.');
  if (parts.length >= 3) {
    return parts[0]; // subdomain
  }

  // Could also handle custom domains here by looking up in database
  return null;
}

/**
 * Middleware to identify and validate tenant based on request
 */
export async function tenantIdentification(req: Request, res: Response, next: NextFunction) {
  try {
    const hostname = req.get('host') || '';
    const tenantSubdomain = extractTenantFromHost(hostname);

    if (tenantSubdomain) {
      // Look up tenant by subdomain
      const tenant = await db
        .select()
        .from(tenants)
        .where(eq(tenants.subdomain, tenantSubdomain))
        .limit(1);

      if (tenant.length === 0) {
        return res.status(404).json({ error: 'Tenant not found' });
      }

      const tenantData = tenant[0];

      // Check if tenant is active
      if (tenantData.status === 'suspended' || tenantData.status === 'cancelled') {
        return res.status(403).json({ error: 'Account suspended' });
      }

      // Add tenant info to request
      req.tenant = {
        id: tenantData.id,
        name: tenantData.name,
        status: tenantData.status,
        subscriptionPackageId: tenantData.subscriptionPackageId,
      };
    }

    next();
  } catch (error) {
    console.error('Tenant identification error:', error);
    res.status(500).json({ error: 'Internal server error' });
  }
}

/**
 * Middleware to ensure user belongs to the current tenant
 */
export function requireTenantUser(req: Request, res: Response, next: NextFunction) {
  if (!req.user) {
    return res.status(401).json({ error: 'Authentication required' });
  }

  if (!req.tenant) {
    return res.status(400).json({ error: 'No tenant context' });
  }

  // Super admin can access any tenant
  if (req.user.role === 'super_admin') {
    return next();
  }

  // User must belong to the current tenant
  if (req.user.tenantId !== req.tenant.id) {
    return res.status(403).json({ error: 'Access denied' });
  }

  next();
}

/**
 * Middleware to require super admin role
 */
export function requireSuperAdmin(req: Request, res: Response, next: NextFunction) {
  if (!req.user || req.user.role !== 'super_admin') {
    return res.status(403).json({ error: 'Super admin access required' });
  }
  next();
}

/**
 * Middleware to require tenant admin role
 */
export function requireTenantAdmin(req: Request, res: Response, next: NextFunction) {
  if (!req.user) {
    return res.status(401).json({ error: 'Authentication required' });
  }

  // Super admin can act as tenant admin
  if (req.user.role === 'super_admin') {
    return next();
  }

  // Must be tenant admin for the current tenant
  if (req.user.role !== 'tenant_admin' || req.user.tenantId !== req.tenant?.id) {
    return res.status(403).json({ error: 'Tenant admin access required' });
  }

  next();
}

/**
 * Helper function to add tenant filter to queries
 */
export function addTenantFilter(req: Request): string | null {
  // Super admin sees all data
  if (req.user?.role === 'super_admin') {
    return null;
  }

  // Return current tenant ID for filtering
  return req.tenant?.id || null;
}

/**
 * Validate that user can only access their tenant's data
 */
export function validateTenantAccess(userTenantId: string | null, requestTenantId: string, req: Request): boolean {
  // Super admin can access any tenant's data
  if (req.user?.role === 'super_admin') {
    return true;
  }

  // User can only access their own tenant's data
  return userTenantId === requestTenantId;
}

server/services/notification-email.ts.content:
import nodemailer from 'nodemailer';

export interface EmailConfig {
  host: string;
  port: number;
  secure?: boolean;
  auth: {
    user: string;
    pass: string;
  };
}

export interface WelcomeEmailData {
  name: string;
  email: string;
  organizationName: string;
  subdomain: string;
  trialDays: number;
  loginUrl: string;
  checkoutUrl?: string;
}

export interface EmailTemplateData {
  to: string;
  from?: string;
  subject: string;
  html: string;
  text?: string;
}

export class NotificationEmailService {
  private transporter: nodemailer.Transporter | null = null;

  constructor() {
    this.initializeTransporter();
  }

  private initializeTransporter() {
    const config: EmailConfig = {
      host: process.env.SMTP_HOST || 'smtp.gmail.com',
      port: parseInt(process.env.SMTP_PORT || '587'),
      secure: false, // true for 465, false for other ports
      auth: {
        user: process.env.SMTP_USER || '',
        pass: process.env.SMTP_PASS || '',
      },
    };

    // Only initialize if credentials are provided
    if (config.auth.user && config.auth.pass) {
      this.transporter = nodemailer.createTransporter(config);
    } else {
      console.warn('Email service not configured - SMTP credentials missing');
    }
  }

  // Allow dynamic configuration (used by super admin config)
  async configure(config: EmailConfig) {
    this.transporter = nodemailer.createTransporter(config);
  }

  async sendEmail(data: EmailTemplateData): Promise<boolean> {
    if (!this.transporter) {
      console.warn('Email service not configured - cannot send email');
      return false;
    }

    try {
      const mailOptions = {
        from: data.from || process.env.SMTP_FROM || process.env.SMTP_USER,
        to: data.to,
        subject: data.subject,
        html: data.html,
        text: data.text,
      };

      const result = await this.transporter.sendMail(mailOptions);
      console.log('Email sent successfully:', result.messageId);
      return true;
    } catch (error) {
      console.error('Failed to send email:', error);
      return false;
    }
  }

  async sendWelcomeEmail(data: WelcomeEmailData): Promise<boolean> {
    const html = this.generateWelcomeEmailHTML(data);
    const text = this.generateWelcomeEmailText(data);

    return this.sendEmail({
      to: data.email,
      subject: `Welcome to VenueFlow, ${data.name}! ðŸŽ‰`,
      html,
      text,
    });
  }

  private generateWelcomeEmailHTML(data: WelcomeEmailData): string {
    return `
<!DOCTYPE html>
<html>
<head>
    <meta charset="utf-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Welcome to VenueFlow</title>
    <style>
        body { font-family: Arial, sans-serif; line-height: 1.6; color: #333; max-width: 600px; margin: 0 auto; padding: 20px; }
        .header { background: linear-gradient(135deg, #667eea 0%, #764ba2 100%); color: white; padding: 30px; text-align: center; border-radius: 10px 10px 0 0; }
        .content { background: #f9f9f9; padding: 30px; border-radius: 0 0 10px 10px; }
        .button { display: inline-block; padding: 12px 24px; background: #4f46e5; color: white; text-decoration: none; border-radius: 6px; margin: 10px 5px; }
        .footer { text-align: center; margin-top: 30px; color: #666; font-size: 14px; }
        .highlight { background: #e0e7ff; padding: 15px; border-radius: 6px; margin: 15px 0; }
    </style>
</head>
<body>
    <div class="header">
        <h1>ðŸŽ‰ Welcome to VenueFlow!</h1>
        <p>Your venue management platform is ready</p>
    </div>

    <div class="content">
        <h2>Hi ${data.name},</h2>

        <p>Congratulations! Your VenueFlow account for <strong>${data.organizationName}</strong> has been successfully created.</p>

        <div class="highlight">
            <h3>ðŸš€ Your Account Details:</h3>
            <ul>
                <li><strong>Organization:</strong> ${data.organizationName}</li>
                <li><strong>Subdomain:</strong> ${data.subdomain}.yourdomain.com</li>
                <li><strong>Email:</strong> ${data.email}</li>
                <li><strong>Trial Period:</strong> ${data.trialDays} days</li>
            </ul>
